module aes_inv_cipher_top (
    input clk,
    input rst,
    input kld,
    input ld,
    output done,
    input [127:0] key,
    input [127:0] text_in,
    output [127:0] text_out
);

// Register declarations
reg [127:0] text_in_r;
reg [7:0] sa [0:3][0:3];
reg [3:0] dcnt;
reg [127:0] kb [10:0];
reg [3:0] kcnt;
reg ld_r;
reg [127:0] text_out_reg;

// Wire declarations
wire [31:0] wo_0, wo_1, wo_2, wo_3;
wire [127:0] w;
wire [7:0] sbox_out [0:3][0:3];
wire [127:0] sa_shift, sa_sub, sa_ark, sa_mix;
wire [3:0] next_dcnt;
wire done_next;

// FSM states
typedef enum {
    IDLE, KeyLoad, KeyExpansion, KeyStored,
    InitialRound, StandardRound, FinalRound, Done
} state_t;
reg [2:0] state;

// Key expansion module instantiation
aes_key_expand_128 key_expand (
    .clk(clk),
    .kld(kld),
    .key(key),
    .wo_0(wo_0),
    .wo_1(wo_1),
    .wo_2(wo_2),
    .wo_3(wo_3)
);

// Inverse S-box instances
genvar i, j;
generate
    for (i=0; i<4; i=i+1) begin : row
        for (j=0; j<4; j=j+1) begin : col
            aes_inv_sbox u_sbox (
                .a(sa[i][j]),
                .b(sbox_out[i][j])
            );
        end
    end
endgenerate

// Key buffer and counter
always @(posedge clk) begin
    if (rst) begin
        kcnt <= 4'hA;
    end else if (state == KeyExpansion) begin
        if (kcnt > 0) begin
            kb[kcnt] <= {wo_3, wo_2, wo_1, wo_0};
            kcnt <= kcnt - 1;
        end
    end
end

// FSM and main control
always @(posedge clk) begin
    if (rst) begin
        state <= IDLE;
        dcnt <= 0;
        ld_r <= 0;
        text_out_reg <= 0;
    end else begin
        case (state)
            IDLE: begin
                if (kld) state <= KeyLoad;
                else if (ld) begin
                    text_in_r <= text_in;
                    state <= InitialRound;
                    dcnt <= 0;
                end
            end
            KeyLoad: state <= KeyExpansion;
            KeyExpansion: begin
                if (kcnt == 0) state <= KeyStored;
            end
            KeyStored: begin
                if (ld) state <= InitialRound;
            end
            InitialRound: begin
                state <= StandardRound;
                dcnt <= dcnt + 1;
            end
            StandardRound: begin
                if (dcnt == 10) state <= FinalRound;
                else dcnt <= dcnt + 1;
            end
            FinalRound: begin
                state <= Done;
                dcnt <= dcnt + 1;
            end
            Done: begin
                text_out_reg <= {sa[0][0], sa[1][0], sa[2][0], sa[3][0],
                                sa[0][1], sa[1][1], sa[2][1], sa[3][1],
                                sa[0][2], sa[1][2], sa[2][2], sa[3][2],
                                sa[0][3], sa[1][3], sa[2][3], sa[3][3]};
                state <= IDLE;
            end
        endcase
        ld_r <= ld;
    end
end

// Data path transformations
always @* begin
    // InvShiftRows
    sa_shift = {
        sa[0][0], sa[3][1], sa[2][2], sa[1][3],
        sa[1][0], sa[0][1], sa[3][2], sa[2][3],
        sa[2][0], sa[1][1], sa[0][2], sa[3][3],
        sa[3][0], sa[2][1], sa[1][2], sa[0][3]
    };

    // InvSubBytes
    for (int i=0; i<4; i++)
        for (int j=0; j<4; j++)
            sa_sub[i][j] = sbox_out[i][j];

    // AddRoundKey
    sa_ark = sa_sub ^ kb[dcnt];

    // InvMixColumns (except final round)
    if (dcnt < 10) begin
        for (int col=0; col<4; col++) begin
            sa_mix[col*32 +: 8] = pmul_e(sa_ark[col*32 +: 8]) ^ pmul_b(sa_ark[col*32+8 +: 8]) ^ pmul_d(sa_ark[col*32+16 +: 8]) ^ pmul_9(sa_ark[col*32+24 +: 8]);
            // ... Similar for other rows
        end
    end else begin
        sa_mix = sa_ark;
    end
end

// State matrix update
always @(posedge clk) begin
    if (state == InitialRound) begin
        // Initial round key addition
        sa <= text_in_r ^ kb[0];
    end else if (state == StandardRound || state == FinalRound) begin
        // Update state with transformed values
        sa <= sa_mix;
    end
end

// Output assignments
assign done = (state == Done);
assign text_out = text_out_reg;

// Galois Field multiplication functions
function [7:0] pmul_e(input [7:0] b);
    // Implementation of 0x0E multiplication
endfunction

function [7:0] pmul_b(input [7:0] b);
    // Implementation of 0x0B multiplication
endfunction

function [7:0] pmul_d(input [7:0] b);
    // Implementation of 0x0D multiplication
endfunction

function [7:0] pmul_9(input [7:0] b);
    // Implementation of 0x09 multiplication
endfunction

endmodule

