module aes_sbox (
    input  [7:0] a,
    output [7:0] b
);

reg [7:0] b_reg;

always @* begin
    case (a)
        8'h00: b_reg = 8'h63;
        8'h01: b_reg = 8'h7c;
        8'h02: b_reg = 8'h77;
        8'h03: b_reg = 8'h7b;
        8'h04: b_reg = 8'hf2;
        8'h05: b_reg = 8'h6b;
        8'h06: b_reg = 8'h6f;
        8'h07: b_reg = 8'hc5;
        8'h08: b_reg = 8'h30;
        8'h09: b_reg = 8'h01;
        8'h0a: b_reg = 8'h67;
        8'h0b: b_reg = 8'h2b;
