        8'h0d: b_reg = 8'hd7;
        8'h0e: b_reg = 8'hab;
        8'h0f: b_reg = 8'h76;
        // ... (All 256 cases must be explicitly listed)
        8'hf0: b_reg = 8'h8c;
        8'hf1: b_reg = 8'ha1;
        8'hf2: b_reg = 8'h89;
        8'hf3: b_reg = 8'h0d;
        8'hf4: b_reg = 8'hbf;
        8'hf5: b_reg = 8'he6;
        8'hf6: b_reg = 8'h42;
