module aes_inv_cipher_top (
    input          clk,
    input          rst,
    input          kld,
    input          ld,
    output reg     done,
    input  [127:0] key,
    input  [127:0] text_in,
    output [127:0] text_out
);

// State machine states
localparam [3:0] IDLE         = 4'd0,
                 KEY_LOAD     = 4'd1,
                 KEY_EXPANSION= 4'd2,
                 KEY_STORED   = 4'd3,
                 INITIAL_ROUND= 4'd4,
                 STANDARD_ROUND=4'd5,
                 FINAL_ROUND  = 4'd6,
                 DONE_STATE   = 4'd7;

reg [3:0] state, next_state;
reg [127:0] text_in_r;
reg [7:0] sa [0:3][0:3];  // State matrix
reg [3:0] dcnt;           // Round counter
reg [127:0] kb [0:10];    // Key buffer
reg [3:0] kcnt;           // Key counter
reg ld_r;

// Key expansion signals
wire [31:0] wo_0, wo_1, wo_2, wo_3;
aes_key_expand_128 key_expand (
    .clk(clk),
    .kld(kld),
    .key(key),
    .wo_0(wo_0),
    .wo_1(wo_1),
    .wo_2(wo_2),
    .wo_3(wo_3)
);

// Inverse S-box instances
wire [7:0] inv_sbox_out [0:3][0:3];
genvar i, j;
generate
    for (i=0; i<4; i=i+1) begin : row
        for (j=0; j<4; j=j+1) begin : col
            aes_inv_sbox u_sbox (
                .a(sa[i][j]),
                .b(inv_sbox_out[i][j])
            );
        end
    end
endgenerate

// Internal signals
wire [127:0] current_key;
reg [127:0] sa_shifted, sa_sub, sa_ark, sa_mix;
reg [3:0] round_type;

// State machine
always @(posedge clk or posedge rst) begin
    if (rst) state <= IDLE;
    else state <= next_state;
end

always @(*) begin
    next_state = state;
    case (state)
        IDLE:          if (kld) next_state = KEY_LOAD;
                       else if (ld) next_state = INITIAL_ROUND;
        KEY_LOAD:      next_state = KEY_EXPANSION;
        KEY_EXPANSION: if (kcnt == 0) next_state = KEY_STORED;
        KEY_STORED:    if (ld) next_state = INITIAL_ROUND;
        INITIAL_ROUND: next_state = STANDARD_ROUND;
        STANDARD_ROUND:if (dcnt == 10) next_state = FINAL_ROUND;
        FINAL_ROUND:   next_state = DONE_STATE;
        DONE_STATE:    next_state = IDLE;
        default:       next_state = IDLE;
    endcase
end

// Key buffer loading
always @(posedge clk or posedge rst) begin
    if (rst) begin
        kcnt <= 4'hA;
        for (integer i=0; i<11; i=i+1) kb[i] <= 0;
    end else if (state == KEY_EXPANSION) begin
        kb[kcnt] <= {wo_3, wo_2, wo_1, wo_0};
        kcnt <= kcnt - 1;
    end
end

// Data path
always @(posedge clk or posedge rst) begin
    if (rst) begin
        text_in_r <= 0;
        dcnt <= 0;
        ld_r <= 0;
        done <= 0;
        for (integer i=0; i<4; i=i+1)
            for (integer j=0; j<4; j=j+1)
                sa[i][j] <= 0;
    end else begin
        ld_r <= ld;
        case (state)
            IDLE: begin
                done <= 0;
                if (ld) text_in_r <= text_in;
            end
            INITIAL_ROUND: begin
                // Load initial state and add round key
                for (integer i=0; i<4; i=i+1)
                    for (integer j=0; j<4; j=j+1)
                        sa[i][j] <= text_in_r[127-8*(i+4*j) -:8] ^ current_key[127-8*(i+4*j) -:8];
                dcnt <= 1;
            end
            STANDARD_ROUND: begin
                // Apply transformations
                sa <= inv_mix_columns(sa_ark);
                dcnt <= dcnt + 1;
            end
            FINAL_ROUND: begin
                // Final round without mix columns
                for (integer i=0; i<4; i=i+1)
                    for (integer j=0; j<4; j=j+1)
                        sa[i][j] <= sa_ark[i][j];
                dcnt <= 11;
                done <= 1;
            end
            DONE_STATE: done <= 0;
        endcase
    end
end

// Inverse ShiftRows
always @(*) begin
    sa_shifted = sa;
    // Row 0: no shift
    // Row 1: right shift 3
    {sa_shifted[1][0], sa_shifted[1][1], sa_shifted[1][2], sa_shifted[1][3]} = 
    {sa[1][3], sa[1][0], sa[1][1], sa[1][2]};
    // Row 2: right shift 2
    {sa_shifted[2][0], sa_shifted[2][1], sa_shifted[2][2], sa_shifted[2][3]} = 
    {sa[2][2], sa[2][3], sa[2][0], sa[2][1]};
    // Row 3: right shift 1
    {sa_shifted[3][0], sa_shifted[3][1], sa_shifted[3][2], sa_shifted[3][3]} = 
    {sa[3][1], sa[3][2], sa[3][3], sa[3][0]};
end

// AddRoundKey
assign current_key = kb[dcnt];
always @(*) begin
    for (integer i=0; i<4; i=i+1)
        for (integer j=0; j<4; j=j+1)
            sa_ark[i][j] = inv_sbox_out[i][j] ^ current_key[127-8*(i+4*j) -:8];
end

// Inverse MixColumns function
function [7:0] inv_mix_column;
    input [7:0] s0, s1, s2, s3;
    begin
        inv_mix_column = 
            pmul_e(s0) ^ pmul_b(s1) ^ pmul_d(s2) ^ pmul_9(s3);
    end
endfunction

function [7:0] pmul_e; input [7:0] b; /* Implementation */ endfunction
function [7:0] pmul_b; input [7:0] b; /* Implementation */ endfunction
function [7:0] pmul_d; input [7:0] b; /* Implementation */ endfunction
function [7:0] pmul_9; input [7:0] b; /* Implementation */ endfunction

// Output mapping
assign text_out = {sa[0][0], sa[1][0], sa[2][0], sa[3][0],
                   sa[0][1], sa[1][1], sa[2][1], sa[3][1],
                   sa[0][2], sa[1][2], sa[2][2], sa[3][2],
                   sa[0][3], sa[1][3], sa[2][3], sa[3][3]};

endmodule

module aes_rcon (
    input clk,
    input kld,
    output reg [31:0] out
);

reg [3:0] rcnt;

always @(posedge clk) begin
    if (kld)
        rcnt <= 4'b0;
    else
        rcnt <= rcnt + 1;
end

always @(posedge clk) begin
    case (rcnt)
        4'd0:  out <= 32'h01000000;
        4'd1:  out <= 32'h02000000;
        4'd2:  out <= 32'h04000000;
        4'd3:  out <= 32'h08000000;
        4'd4:  out <= 32'h10000000;
        4'd5:  out <= 32'h20000000;
        4'd6:  out <= 32'h40000000;
        4'd7:  out <= 32'h80000000;
        4'd8:  out <= 32'h1B000000;
        4'd9:  out <= 32'h36000000;
        default: out <= 32'h00000000;
    endcase
end

endmodule

module aes_inv_cipher_top (
    input         clk,
    input         rst,
    input         kld,
    input         ld,
    output        done,
    input  [127:0] key,
    input  [127:0] text_in,
    output [127:0] text_out
);

// State definitions
localparam IDLE         = 0;
localparam KEY_LOAD     = 1;
localparam KEY_EXPAND   = 2;
localparam KEY_STORED   = 3;
localparam INITIAL_ROUND= 4;
localparam STANDARD_ROUND=5;
localparam FINAL_ROUND  = 6;
localparam DONE_STATE   = 7;

reg [2:0] state, next_state;
reg [3:0] dcnt;
reg [3:0] kcnt;
reg [127:0] text_in_r;
reg [127:0] kb [10:0];
reg ld_r;
reg [7:0] sa [0:3][0:3];
reg [127:0] text_out_reg;

wire [31:0] wo_0, wo_1, wo_2, wo_3;
wire [127:0] round_key;
wire [7:0] inv_sbox_out [0:3][0:3];
wire [7:0] shifted_rows [0:3][0:3];
wire [7:0] ark [0:3][0:3];
wire [7:0] mix_col_out [0:3][0:3];

// Key expansion module
aes_key_expand_128 key_expand (
    .clk(clk),
    .kld(kld),
    .key(key),
    .wo_0(wo_0),
    .wo_1(wo_1),
    .wo_2(wo_2),
    .wo_3(wo_3)
);

// Inverse S-box instances
genvar i, j;
generate
    for (i=0; i<4; i=i+1) begin : row
        for (j=0; j<4; j=j+1) begin : col
            aes_inv_sbox sbox_inst (
                .a(shifted_rows[i][j]),
                .b(inv_sbox_out[i][j])
            );
        end
    end
endgenerate

// State machine
always @(posedge clk or posedge rst) begin
    if (rst) state <= IDLE;
    else state <= next_state;
end

always @(*) begin
    case (state)
        IDLE:          next_state = kld ? KEY_LOAD : ld ? KEY_STORED : IDLE;
        KEY_LOAD:      next_state = KEY_EXPAND;
        KEY_EXPAND:    next_state = (kcnt == 0) ? KEY_STORED : KEY_EXPAND;
        KEY_STORED:    next_state = ld ? INITIAL_ROUND : KEY_STORED;
        INITIAL_ROUND: next_state = STANDARD_ROUND;
        STANDARD_ROUND:next_state = (dcnt == 10) ? FINAL_ROUND : STANDARD_ROUND;
        FINAL_ROUND:   next_state = (dcnt == 11) ? DONE_STATE : FINAL_ROUND;
        DONE_STATE:    next_state = IDLE;
        default:       next_state = IDLE;
    endcase
end

// Key buffer loading
always @(posedge clk) begin
    if (state == KEY_EXPAND) begin
        kb[kcnt] <= {wo_3, wo_2, wo_1, wo_0};
        kcnt <= kcnt - 1;
    end
    else if (state == IDLE) kcnt <= 4'hA;
end

// Data processing
always @(posedge clk) begin
    if (rst) begin
        dcnt <= 0;
        text_out_reg <= 0;
        ld_r <= 0;
    end else begin
        ld_r <= ld;
        case (state)
            INITIAL_ROUND: begin
                // Load initial data and add round key
                if (ld_r) begin
                    // Map text_in_r to state matrix
                    // Implementation omitted for brevity
                    dcnt <= dcnt + 1;
                end
            end
            STANDARD_ROUND: begin
                // Perform transformations
                // InvShiftRows, InvSubBytes, AddRoundKey, InvMixColumns
                dcnt <= dcnt + 1;
            end
            FINAL_ROUND: begin
                // Final round without InvMixColumns
                dcnt <= dcnt + 1;
            end
            DONE_STATE: begin
                // Map state to text_out
                text_out_reg <= {sa[0][0], sa[1][0], sa[2][0], sa[3][0], 
                                sa[0][1], sa[1][1], sa[2][1], sa[3][1],
                                sa[0][2], sa[1][2], sa[2][2], sa[3][2],
                                sa[0][3], sa[1][3], sa[2][3], sa[3][3]};
                dcnt <= 0;
            end
        endcase
    end
end

// Round key selection
assign round_key = kb[dcnt];

// InvShiftRows implementation
assign shifted_rows[0][0] = sa[0][0];
assign shifted_rows[0][1] = sa[0][1];
assign shifted_rows[0][2] = sa[0][2];
assign shifted_rows[0][3] = sa[0][3];

assign shifted_rows[1][0] = sa[1][3];
assign shifted_rows[1][1] = sa[1][0];
assign shifted_rows[1][2] = sa[1][1];
assign shifted_rows[1][3] = sa[1][2];

assign shifted_rows[2][0] = sa[2][2];
assign shifted_rows[2][1] = sa[2][3];
assign shifted_rows[2][2] = sa[2][0];
assign shifted_rows[2][3] = sa[2][1];

assign shifted_rows[3][0] = sa[3][1];
assign shifted_rows[3][1] = sa[3][2];
assign shifted_rows[3][2] = sa[3][3];
assign shifted_rows[3][3] = sa[3][0];

// AddRoundKey implementation
genvar x, y;
generate
    for (x=0; x<4; x=x+1) begin : add_key_x
        for (y=0; y<4; y=y+1) begin : add_key_y
