// State machine states
typedef enum {
    IDLE, KeyLoad, KeyExpansion, KeyStored,
    InitialRound, StandardRound, FinalRound, Done
} state_t;
reg [3:0] state;

// Key buffer control
always @(posedge clk or posedge rst) begin
    if (rst) begin
        kcnt <= 4'hA;
    end else if (state == KeyExpansion) begin
