// State machine transitions
always @(posedge clk or posedge rst) begin
    if (rst) begin
        state <= IDLE;
        dcnt <= 0;
        done <= 0;
        ld_r <= 0;
    end else begin
        case (state)
            IDLE: begin
                if (kld) state <= KeyLoad;
                else if (ld) begin
                    text_in_r <= text_in;
                    state <= InitialRound;
                    dcnt <= 0;
                    ld_r <= 1;
                end
            end
            KeyLoad: state <= KeyExpansion;
            KeyExpansion: if (kcnt == 0) state <= KeyStored;
            KeyStored: if (ld) state <= InitialRound;
            InitialRound: begin
                state <= StandardRound;
                dcnt <= dcnt + 1;
                ld_r <= 0;
            end
            StandardRound: begin
                if (dcnt == 10) state <= FinalRound;
                else dcnt <= dcnt + 1;
            end
            FinalRound: begin
                state <= Done;
                dcnt <= dcnt + 1;
            end
            Done: begin
                done <= 1;
                state <= IDLE;
            end
        endcase
    end
end

// InvShiftRows transformation
assign sa_sr[0][0] = sa[0][0];
assign sa_sr[0][1] = sa[0][1];
assign sa_sr[0][2] = sa[0][2];
assign sa_sr[0][3] = sa[0][3];
assign sa_sr[1][0] = sa[1][3];
assign sa_sr[1][1] = sa[1][0];
assign sa_sr[1][2] = sa[1][1];
assign sa_sr[1][3] = sa[1][2];
assign sa_sr[2][0] = sa[2][2];
assign sa_sr[2][1] = sa[2][3];
assign sa_sr[2][2] = sa[2][0];
assign sa_sr[2][3] = sa[2][1];
assign sa_sr[3][0] = sa[3][1];
assign sa_sr[3][1] = sa[3][2];
assign sa_sr[3][2] = sa[3][3];
assign sa_sr[3][3] = sa[3][0];

