wire [127:0] round_key = kb[dcnt];
generate
    for (i=0; i<4; i=i+1) begin
        for (j=0; j<4; j=j+1) begin
            assign sa_ark[i][j] = sa_sub[i][j] ^ round_key[127 - (i*32 + j*8 + 7) -: 8];
        end
