endgenerate

// InvMixColumns transformation
generate
    for (j=0; j<4; j=j+1) begin : mix_col
        wire [7:0] b0 = sa_ark[0][j];
        wire [7:0] b1 = sa_ark[1][j];
        wire [7:0] b2 = sa_ark[2][j];
        wire [7:0] b3 = sa_ark[3][j];

        assign sa_next[0][j] = pmul_e(b0) ^ pmul_b(b1) ^ pmul_d(b2) ^ pmul_9(b3);
        assign sa_next[1][j] = pmul_9(b0) ^ pmul_e(b1) ^ pmul_b(b2) ^ pmul_d(b3);
        assign sa_next[2][j] = pmul_d(b0) ^ pmul_9(b1) ^ pmul_e(b2) ^ pmul_b(b3);
        assign sa_next[3][j] = pmul_b(b0) ^ pmul_d(b1) ^ pmul_9(b2) ^ pmul_e(b3);
    end
endgenerate

// State matrix update
always @(posedge clk) begin
    if (rst) begin
        for (integer i=0; i<4; i=i+1)
            for (integer j=0; j<4; j=j+1)
                sa[i][j] <= 0;
    end else if (ld_r) begin
        // Initial load with AddRoundKey
        sa[0][0] <= text_in_r[127:120] ^ round_key[127:120];
        sa[1][0] <= text_in_r[119:112] ^ round_key[119:112];
        // ... (similar mapping for all state elements)
    end else if (state == StandardRound) begin
        // Apply InvMixColumns
        for (integer i=0; i<4; i=i+1)
            for (integer j=0; j<4; j=j+1)
                sa[i][j] <= sa_next[i][j];
    end else if (state == FinalRound) begin
        // Bypass InvMixColumns
        for (integer i=0; i<4; i=i+1)
            for (integer j=0; j<4; j=j+1)
                sa[i][j] <= sa_ark[i][j];
    end
end

// Output mapping
always @(posedge clk) begin
    if (state == Done) begin
        text_out <= {sa[0][0], sa[1][0], sa[2][0], sa[3][0],
                     sa[0][1], sa[1][1], sa[2][1], sa[3][1],
                     sa[0][2], sa[1][2], sa[2][2], sa[3][2],
