    if (rst) begin
        text_in_r <= 0;
        ld_r <= 0;
        dcnt <= 0;
        sa_next <= 0;
        text_out_reg <= 0;
    end else begin
        case (state)
            IDLE: begin
                if (ld) text_in_r <= text_in;
                ld_r <= ld;
