                     sa[0][3], sa[1][3], sa[2][3], sa[3][3]} <= text_in_r ^ kb[10];
                    dcnt <= 1;
                end
            end

            INITIAL_ROUND: begin
                // InvShiftRows & InvSubBytes
                {sa[0][0], sa[1][0], sa[2][0], sa[3][0],
                 sa[0][1], sa[1][1], sa[2][1], sa[3][1],
                 sa[0][2], sa[1][2], sa[2][2], sa[3][2],
                 sa[0][3], sa[1][3], sa[2][3], sa[3][3]} <= sa_shift;
                dcnt <= dcnt + 1;
            end

            STANDARD_ROUND: begin
                // Apply InvMixColumns
                sa_next <= sa_mix;
                dcnt <= dcnt + 1;
            end

            FINAL_ROUND: begin
                // Bypass InvMixColumns
                text_out_reg <= {sa_ark[0][0], sa_ark[1][0], sa_ark[2][0], sa_ark[3][0],
                                sa_ark[0][1], sa_ark[1][1], sa_ark[2][1], sa_ark[3][1],
