module PulseGenerator (
    input clk,
    input rst_n,
    input enable,
    input [31:0] pulse_interval,
    input [31:0] pulse_width,
    output reg pulse_out,
    output status
);

// State definitions
localparam [1:0]
    IDLE = 2'b00,
    PULSE_HIGH = 2'b01,
    INTERVAL = 2'b10;

reg [1:0] state_reg, next_state;
reg [31:0] counter_reg;

// State transition logic
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        state_reg <= IDLE;
        counter_reg <= 32'b0;
    end
    else begin
        state_reg <= next_state;
        counter_reg <= (state_reg != next_state) ? 32'b0 : counter_reg + 1;
    end
end

// Next state logic
always @(*) begin
    next_state = state_reg;
    case (state_reg)
        IDLE: begin
            if (enable)
                next_state = PULSE_HIGH;
        end
        PULSE_HIGH: begin
            if (counter_reg >= pulse_width - 1)
                next_state = INTERVAL;
        end
        INTERVAL: begin
            if (counter_reg >= pulse_interval - 1)
                next_state = enable ? PULSE_HIGH : IDLE;
        end
        default: next_state = IDLE;
    endcase
end

// Output logic
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        pulse_out <= 1'b0;
    end
    else begin
        pulse_out <= (state_reg == PULSE_HIGH);
    end
end

assign status = (state_reg == PULSE_HIGH);

endmodule

