module PulseGenerator (
    input clk,
    input rst_n,
    input start,
    input [31:0] duration,
    output reg pulse
);

reg [1:0] state;
reg [31:0] counter;
reg start_prev;

localparam IDLE     = 2'b00;
localparam COUNTING = 2'b01;

wire start_rise = start && !start_prev;

always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        state <= IDLE;
        counter <= 0;
        pulse <= 0;
        start_prev <= 0;
    end else begin
        start_prev <= start;
        case (state)
            IDLE: begin
                pulse <= 0;
                if (start_rise) begin
                    state <= COUNTING;
                    counter <= duration;
                end
            end

            COUNTING: begin
                if (start_rise) begin
                    counter <= duration;
                end
                else if (counter > 0) begin
                    counter <= counter - 1;
                end

                if (counter == 0) begin
                    pulse <= 1;
                    state <= IDLE;
                end else begin
                    pulse <= 0;
                end
            end
        endcase
    end
end

endmodule

