module RippleCarryAdder (
    input clk,
    input [3:0] A,
    input [3:0] B,
    input Cin,
    output reg [3:0] Sum,
    output reg Cout
);

wire [3:0] sum_comb;
wire [4:0] carry;

assign carry[0] = Cin;

genvar i;
generate
    for (i = 0; i < 4; i = i + 1) begin : adder_loop
        assign sum_comb[i] = A[i] ^ B[i] ^ carry[i];
        assign carry[i+1] = (A[i] & B[i]) | (A[i] & carry[i]) | (B[i] & carry[i]);
    end
endgenerate

always @(posedge clk) begin
    Sum <= sum_comb;
    Cout <= carry[4];
end

endmodule

