module uart_tx (
    input wire clk,
    input wire rst_n,
    input wire tx_start,
    input wire [7:0] data_in,
    output reg tx,
    output wire tx_done
);

parameter CLK_FREQ = 100_000_000;  // 100 MHz system clock
parameter BAUD_RATE = 115_200;
localparam BAUD_PERIOD = CLK_FREQ / BAUD_RATE;

typedef enum logic [1:0] {
    IDLE,
    START,
    DATA,
    STOP
} state_t;

reg [1:0] state;
reg [15:0] baud_counter;
reg [2:0] bit_counter;
reg [7:0] data_reg;
reg done;

assign tx_done = done;

always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        state <= IDLE;
        tx <= 1'b1;
        done <= 1'b0;
        baud_counter <= 0;
        bit_counter <= 0;
        data_reg <= 0;
    end else begin
        case (state)
            IDLE: begin
                tx <= 1'b1;
                done <= 1'b0;
                if (tx_start) begin
                    state <= START;
                    data_reg <= data_in;
                    baud_counter <= 0;
                end
            end

            START: begin
                tx <= 1'b0;
                if (baud_counter == BAUD_PERIOD - 1) begin
                    state <= DATA;
                    baud_counter <= 0;
                    bit_counter <= 0;
                end else begin
                    baud_counter <= baud_counter + 1;
                end
            end

            DATA: begin
                tx <= data_reg[bit_counter];
                if (baud_counter == BAUD_PERIOD - 1) begin
                    baud_counter <= 0;
                    if (bit_counter == 7) begin
                        state <= STOP;
                    end else begin
                        bit_counter <= bit_counter + 1;
                    end
                end else begin
                    baud_counter <= baud_counter + 1;
                end
            end

            STOP: begin
                tx <= 1'b1;
                if (baud_counter == BAUD_PERIOD - 1) begin
                    state <= IDLE;
                    done <= 1'b1;
                    baud_counter <= 0;
                end else begin
                    baud_counter <= baud_counter + 1;
                end
            end
        endcase
    end
end

endmodule

module example_counter (
    input clk,
    input rst_n,
    input enable,
    output reg [3:0] count
);

// Async active-low reset with clocked behavior
always @(posedge clk or negedge rst_n) begin
    if (!rst_n) begin
        count <= 4'b0;
    end
    else begin
        count <= (enable) ? count + 1 : count;
    end
end

endmodule

