module adder_4bit (
    input [3:0] A,
    input [3:0] B,
    input carry_in,
    output [3:0] sum,
    output carry_out
);

    // Concatenate carry_out and sum using bitwise addition
    wire [4:0] result_temp;
    assign result_temp = A + B + carry_in;

    assign sum = result_temp[3:0];
    assign carry_out = result_temp[4];

endmodule
