module aes_cipher_top (
    input clk,
    input rst,
    input ld,
    output done,
    input [127:0] key,
    input [127:0] text_in,
    output [127:0] text_out
);

    // Interface with key expansion module
    wire [31:0] wo_0;
    wire [31:0] wo_1;
    wire [31:0] wo_2;
    wire [31:0] wo_3;
    aes_key_expand_128 key_expansion (
        .clk(clk),
        .kld(ld),
        .key(key),
        .wo_0(wo_0),
        .wo_1(wo_1),
        .wo_2(wo_2),
        .wo_3(wo_3)
    );

    // Interface with S-box
    wire [7:0] sbox_out [0:15];
    aes_sbox sbox (
        .a(text_in[7:0]),
        .b(sbox_out[0])
    );
    aes_sbox sbox1 (
        .a(text_in[15:8]),
        .b(sbox_out[1])
    );
    aes_sbox sbox2 (
        .a(text_in[23:16]),
        .b(sbox_out[2])
    );
    aes_sbox sbox3 (
        .a(text_in[31:24]),
        .b(sbox_out[3])
    );
    aes_sbox sbox4 (
        .a(text_in[39:32]),
        .b(sbox_out[4])
    );
    aes_sbox sbox5 (
        .a(text_in[47:40]),
        .b(sbox_out[5])
    );
    aes_sbox sbox6 (
        .a(text_in[55:48]),
        .b(sbox_out[6])
    );
    aes_sbox sbox7 (
        .a(text_in[63:56]),
        .b(sbox_out[7])
    );
    aes_sbox sbox8 (
        .a(text_in[71:64]),
        .b(sbox_out[8])
    );
    aes_sbox sbox9 (
        .a(text_in[79:72]),
        .b(sbox_out[9])
    );
    aes_sbox sbox10 (
        .a(text_in[87:80]),
        .b(sbox_out[10])
    );
    aes_sbox sbox11 (
        .a(text_in[95:88]),
        .b(sbox_out[11])
    );
    aes_sbox sbox12 (
        .a(text_in[103:96]),
        .b(sbox_out[12])
    );
    aes_sbox sbox13 (
        .a(text_in[111:104]),
        .b(sbox_out[13])
    );
    aes_sbox sbox14 (
        .a(text_in[119:112]),
        .b(sbox_out[14])
    );
    aes_sbox sbox15 (
        .a(text_in[127:120]),
        .b(sbox_out[15])
    );

    // Registers
    reg [127:0] text_in_r;
    reg [7:0] sa [0:3][0:3];
    reg [3:0] dcnt;
    reg ld_r;
    reg [127:0] text_out;

    // State transition
    always @(posedge clk) begin
        if (rst) begin
            text_in_r <= 0;
            sa <= 0;
            dcnt <= 0;
            ld_r <= 0;
            text_out <= 0;
        end else begin
            if (ld) begin
                text_in_r <= text_in;
                sa[0][0] <= wo_0;
                sa[1][0] <= wo_1;
                sa[2][0] <= wo_2;
                sa[3][0] <= wo_3;
                dcnt <= 11;
                ld_r <= 1;
            end else begin
                if (dcnt > 0) begin
                    sa[0][1] <= sa[0][0];
                    sa[1][1] <= sa[1][0];
                    sa[2][1] <= sa[2][0];
                    sa[3][1] <= sa[3][0];
                    sa[0][2] <= sa[0][1];
                    sa[1][2] <= sa[1][1];
                    sa[2][2] <= sa[2][1];
                    sa[3][2] <= sa[3][1];
                    sa[0][3] <= sa[0][2];
                    sa[1][3] <= sa[1][2];
                    sa[2][3] <= sa[2][2];
                    sa[3][3] <= sa[3][2];
                    sa[0][0] <= sa[0][3] ^ sbox_out[0] ^ wo_0;
                    sa[1][0] <= sa[1][3] ^ sbox_out[1] ^ wo_1;
                    sa[2][0] <= sa[2][3] ^ sbox_out[2] ^ wo_2;
                    sa[3][0] <= sa[3][3] ^ sbox_out[3] ^ wo_3;
                    dcnt <= dcnt - 1;
                end else begin
                    sa[0][1] <= sa[0][0];
                    sa[1][1] <= sa[1][0];
                    sa[2][1] <= sa[2][0];
                    sa[3][1] <= sa[3][0];
                    sa[0][2] <= sa[0][1];
                    sa[1][2] <= sa[1][1];
                    sa[2][2] <= sa[2][1];
                    sa[3][2] <= sa[3][1];
                    sa[0][3] <= sa[0][2];
                    sa[1][3] <= sa[1][2];
                    sa[2][3] <= sa[2][2];
                    sa[3][3] <= sa[3][2];
                    sa[0][0] <= sa[0][3] ^ sbox_out[0] ^ wo_0;
                    sa[1][0] <= sa[1][3] ^ sbox_out[1] ^ wo_1;
                    sa[2][0] <= sa[2][3] ^ sbox_out[2] ^ wo_2;
                    sa[3][0] <= sa[3][3] ^ sbox_out[3] ^ wo_3;
                    text_out <= {sa[0][0], sa[1][0], sa[2][0], sa[3][0]};
                    done <= 1;
                end
            end
        end
    end

endmodule

module aes_cipher_top(
    input clk,
    input rst,
    input ld,
    output done,
    input [127:0] key,
    input [127:0] text_in,
    output [127:0] text_out
);

    // Interface with aes_key_expand_128 module
    wire [31:0] wo_0;
    wire [31:0] wo_1;
    wire [31:0] wo_2;
    wire [31:0] wo_3;
    aes_key_expand_128 key_expansion(
        .clk(clk),
        .kld(ld),
        .key(key),
        .wo_0(wo_0),
        .wo_1(wo_1),
        .wo_2(wo_2),
        .wo_3(wo_3)
    );

    // Interface with aes_sbox module
    wire [7:0] sbox_out;
    aes_sbox sbox(
        .a(text_in[7:0]),
        .b(sbox_out)
    );

    // Registers
    reg [127:0] text_in_r;
    reg [7:0] sa[0:3][0:3];
    reg [3:0] dcnt;
    reg ld_r;
    reg [127:0] text_out;

    // Control logic
    always @(posedge clk) begin
        if (rst) begin
            dcnt <= 4'b1011;
            ld_r <= 1'b0;
        end else begin
            if (ld) begin
                text_in_r <= text_in;
                ld_r <= 1'b1;
            end else begin
                ld_r <= 1'b0;
            end
            if (dcnt != 4'b0000) begin
                dcnt <= dcnt - 1;
            end
        end
    end

    // State transition logic
    always @(posedge clk) begin
        if (ld_r) begin
            sa[0][0] <= text_in_r[7:0];
            sa[0][1] <= text_in_r[15:8];
            sa[0][2] <= text_in_r[23:16];
            sa[0][3] <= text_in_r[31:24];
            sa[1][0] <= text_in_r[39:32];
            sa[1][1] <= text_in_r[47:40];
            sa[1][2] <= text_in_r[55:48];
            sa[1][3] <= text_in_r[63:56];
            sa[2][0] <= text_in_r[71:64];
            sa[2][1] <= text_in_r[79:72];
            sa[2][2] <= text_in_r[87:80];
            sa[2][3] <= text_in_r[95:88];
            sa[3][0] <= text_in_r[103:96];
            sa[3][1] <= text_in_r[111:104];
            sa[3][2] <= text_in_r[119:112];
            sa[3][3] <= text_in_r[127:120];
        end else begin
            sa[0][0] <= sa[0][0] ^ wo_0;
            sa[0][1] <= sa[0][1] ^ wo_1;
            sa[0][2] <= sa[0][2] ^ wo_2;
            sa[0][3] <= sa[0][3] ^ wo_3;
            sa[1][0] <= sa[1][0] ^ wo_0;
            sa[1][1] <= sa[1][1] ^ wo_1;
            sa[1][2] <= sa[1][2] ^ wo_2;
            sa[1][3] <= sa[1][3] ^ wo_3;
            sa[2][0] <= sa[2][0] ^ wo_0;
            sa[2][1] <= sa[2][1] ^ wo_1;
            sa[2][2] <= sa[2][2] ^ wo_2;
            sa[2][3] <= sa[2][3] ^ wo_3;
            sa[3][0] <= sa[3][0] ^ wo_0;
            sa[3][1] <= sa[3][1] ^ wo_1;
            sa[3][2] <= sa[3][2] ^ wo_2;
            sa[3][3] <= sa[3][3] ^ wo_3;
        end
    end

    // Output logic
    always @(posedge clk) begin
        if (dcnt == 4'b0000) begin
            text_out <= {sa[3][3], sa[3][2], sa[3][1], sa[3][0], sa[2][3], sa[2][2], sa[2][1], sa[2][0], sa[1][3], sa[1][2], sa[1][1], sa[1][0], sa[0][3], sa[0][2], sa[0][1], sa[0][0]};
            done <= 1'b1;
        end else begin
            done <= 1'b0;
        end
    end

endmodule

