module aes_cipher_top (
    input clk,
    input rst,
    input ld,
    output done,
    input [127:0] key,
    input [127:0] text_in,
    output [127:0] text_out
);

    // Interface
    wire [127:0] text_in_r;
    wire [7:0] sa[0:3][0:3];
    wire [3:0] dcnt;
    wire ld_r;

    // Registers
    reg [127:0] text_in_r;
    reg [7:0] sa[0:3][0:3];
    reg [3:0] dcnt;
    reg ld_r;

    // Output
    assign done = (dcnt == 0);
    assign text_out = sa[3][0] ^ sa[3][1] ^ sa[3][2] ^ sa[3][3];

    // Data Loading
    always @(posedge clk) begin
        if (rst) begin
            text_in_r <= 0;
            ld_r <= 0;
        end else if (ld) begin
            text_in_r <= text_in;
            ld_r <= 1;
        end else begin
            ld_r <= 0;
        end
    end

    // Round Counter
    always @(posedge clk) begin
        if (rst) begin
            dcnt <= 11;
        end else if (dcnt > 0) begin
            dcnt <= dcnt - 1;
        end
    end

    // State Matrix
    aes_key_expand_128 key_exp(
        .clk(clk),
        .kld(ld_r),
        .key(key),
        .wo_0(sa[0][0]),
        .wo_1(sa[1][0]),
        .wo_2(sa[2][0]),
        .wo_3(sa[3][0])
    );

    // Initial Round Key Addition
    always @(posedge clk) begin
        if (rst) begin
            sa[0][0] <= 0;
            sa[1][0] <= 0;
            sa[2][0] <= 0;
            sa[3][0] <= 0;
        end else if (ld_r) begin
            sa[0][0] <= text_in_r[127:120] ^ sa[0][0];
            sa[1][0] <= text_in_r[119:112] ^ sa[1][0];
            sa[2][0] <= text_in_r[111:104] ^ sa[2][0];
            sa[3][0] <= text_in_r[103:96] ^ sa[3][0];
        end
    end

    // Rounds
    genvar i;
    generate
        for (i = 1; i < 10; i = i + 1) begin : round
            aes_round_op round_op(
                .clk(clk),
                .rst(rst),
                .ld(ld_r),
                .dcnt(dcnt),
                .sa(sa),
                .text_in_r(text_in_r),
                .text_out(text_out)
            );
        end
    endgenerate

    // Final Round
    aes_round_op final_round(
        .clk(clk),
        .rst(rst),
        .ld(ld_r),
        .dcnt(dcnt),
        .sa(sa),
        .text_in_r(text_in_r),
        .text_out(text_out)
    );

endmodule

