    aes_sbox sbox_6 (
        .a(sa[1][2]),
        .b(sbox_out_6)
    );
    aes_sbox sbox_7 (
        .a(sa[1][3]),
        .b(sbox_out_7)
    );
    aes_sbox sbox_8 (
        .a(sa[2][0]),
        .b(sbox_out_8)
    );
    aes_sbox sbox_9 (
        .a(sa[2][1]),
        .b(sbox_out_9)
    );
    aes_sbox sbox_10 (
        .a(sa[2][2]),
        .b(sbox_out_10)
    );
    aes_sbox sbox_11 (
        .a(sa[2][3]),
        .b(sbox_out_11)
    );
    aes_sbox sbox_12 (
        .a(sa[3][0]),
        .b(sbox_out_12)
    );
    aes_sbox sbox_13 (
        .a(sa[3][1]),
        .b(sbox_out_13)
    );
    aes_sbox sbox_14 (
        .a(sa[3][2]),
        .b(sbox_out_14)
    );
    aes_sbox sbox_15 (
        .a(sa[3][3]),
        .b(sbox_out_15)
    );

    // Column mixing
    wire [7:0] mix_out_0;
    wire [7:0] mix_out_1;
    wire [7:0] mix_out_2;
    wire [7:0] mix_out_3;
    aes_mix_column mix_0 (
        .a({sa[0][0], sa[1][0], sa[2][0], sa[3][0]}),
        .b({mix_out_0, mix_out_1, mix_out_2, mix_out_3})
    );
    aes_mix_column mix_1 (
        .a({sa[0][1], sa[1][1], sa[2][1], sa[3][1]}),
        .b({mix_out_0, mix_out_1, mix_out_2, mix_out_3})
    );
    aes_mix_column mix_2 (
        .a({sa[0][2], sa[1][2], sa[2][2], sa[3][2]}),
        .b({mix_out_0, mix_out_1, mix_out_2, mix_out_3})
    );
    aes_mix_column mix_3 (
        .a({sa[0][3], sa[1][3], sa[2][3], sa[3][3]}),
        .b({mix_out_0, mix_out_1, mix_out_2, mix_out_3})
    );

    // Output generation
    always @(posedge clk) begin
        if (dcnt == 4'b1011) begin
            text_out <= 128'h0;
            done <= 1'b0;
        end else if (dcnt == 4'b1010) begin
            text_out <= {sa[3][3], sa[3][2], sa[3][1], sa[3][0], sa[2][3], sa[2][2], sa[2][1], sa[2][0], sa[1][3], sa[1][2], sa[1][1], sa[1][0], sa[0][3], sa[0][2], sa[0][1], sa[0][0]};
            done <= 1'b1;
        end else begin
            text_out <= 128'h0;
            done <= 1'b0;
        end
    end

endmodule

module aes_cipher_top (
    input clk,
    input rst,
    input ld,
    output done,
    input [127:0] key,
    input [127:0] text_in,
    output [127:0] text_out
);

    // Interface with key expansion module
    wire [31:0] wo_0;
    wire [31:0] wo_1;
    wire [31:0] wo_2;
    wire [31:0] wo_3;

    aes_key_expand_128 key_expansion (
        .clk(clk),
        .kld(ld),
        .key(key),
        .wo_0(wo_0),
        .wo_1(wo_1),
        .wo_2(wo_2),
        .wo_3(wo_3)
    );

    // Interface with S-box
    wire [7:0] sbox_out [0:15];

    aes_sbox sbox (
        .a(text_in[7:0]),
        .b(sbox_out[0])
    );

    // Register array for state matrix
    reg [7:0] sa [0:3][0:3];

    // Register for round counter
    reg [3:0] dcnt;

    // Register for load operation flag
    reg ld_r;

    // Register for input text during encryption process
    reg [127:0] text_in_r;

    // Register for output ciphertext result after complete encryption process
    reg [127:0] text_out;

    // State machine for encryption process
    always @(posedge clk) begin
        if (rst) begin
            dcnt <= 4'b1011;
            ld_r <= 1'b0;
            text_in_r <= 128'h0;
            text_out <= 128'h0;
            sa[0][0] <= 8'h0;
            sa[0][1] <= 8'h0;
            sa[0][2] <= 8'h0;
            sa[0][3] <= 8'h0;
            sa[1][0] <= 8'h0;
            sa[1][1] <= 8'h0;
            sa[1][2] <= 8'h0;
            sa[1][3] <= 8'h0;
            sa[2][0] <= 8'h0;
            sa[2][1] <= 8'h0;
            sa[2][2] <= 8'h0;
            sa[2][3] <= 8'h0;
            sa[3][0] <= 8'h0;
            sa[3][1] <= 8'h0;
            sa[3][2] <= 8'h0;
            sa[3][3] <= 8'h0;
        end else begin
            case (dcnt)
                4'b1011: begin // IDLE
                    if (ld) begin
                        dcnt <= 4'b0000;
                        ld_r <= 1'b1;
                        text_in_r <= text_in;
                    end
                end
                4'b0000: begin // INIT_ROUND
                    if (ld_r) begin
                        dcnt <= 4'b0001;
                        ld_r <= 1'b0;
                        sa[0][0] <= text_in_r[7:0];
                        sa[0][1] <= text_in_r[15:8];
                        sa[0][2] <= text_in_r[23:16];
                        sa[0][3] <= text_in_r[31:24];
                        sa[1][0] <= text_in_r[39:32];
                        sa[1][1] <= text_in_r[47:40];
                        sa[1][2] <= text_in_r[55:48];
                        sa[1][3] <= text_in_r[63:56];
                        sa[2][0] <= text_in_r[71:64];
                        sa[2][1] <= text_in_r[79:72];
                        sa[2][2] <= text_in_r[87:80];
                        sa[2][3] <= text_in_r[95:88];
                        sa[3][0] <= text_in_r[103:96];
                        sa[3][1] <= text_in_r[111:104];
                        sa[3][2] <= text_in_r[119:112];
                        sa[3][3] <= text_in_r[127:120];
                        sa[0][0] ^= wo_0;
                        sa[1][1] ^= wo_1;
                        sa[2][2] ^= wo_2;
                        sa[3][3] ^= wo_3;
                    end
                end
                4'b0001: begin // ROUND_OP
                    if (ld_r) begin
                        dcnt <= dcnt - 1;
                        ld_r <= 1'b0;
                        sa[0][0] <= sbox_out[sa[0][0]];
                        sa[0][1] <= sbox_out[sa[0][1]];
                        sa[0][2] <= sbox_out[sa[0][2]];
                        sa[0][3] <= sbox_out[sa[0][3]];
                        sa[1][0] <= sbox_out[sa[1][0]];
                        sa[1][1] <= sbox_out[sa[1][1]];
                        sa[1][2] <= sbox_out[sa[1][2]];
                        sa[1][3] <= sbox_out[sa[1][3]];
                        sa[2][0] <= sbox_out[sa[2][0]];
                        sa[2][1] <= sbox_out[sa[2][1]];
                        sa[2][2] <= sbox_out[sa[2][2]];
                        sa[2][3] <= sbox_out[sa[2][3]];
                        sa[3][0] <= sbox_out[sa[3][0]];
                        sa[3][1] <= sbox_out[sa[3][1]];
                        sa[3][2] <= sbox_out[sa[3][2]];
                        sa[3][3] <= sbox_out[sa[3][3]];
                        sa[0][0] ^= wo_0;
                        sa[1][1] ^= wo_1;
                        sa[2][2] ^= wo_2;
                        sa[3][3] ^= wo_3;
                    end
                end
                4'b0010: begin // FINAL_ROUND
                    if (ld_r) begin
                        dcnt <= dcnt - 1;
                        ld_r <= 1'b0;
                        sa[0][0] <= sbox_out[sa[0][0]];
                        sa[0][1] <= sbox_out[sa[0][1]];
                        sa[0][2] <= sbox_out[sa[0][2]];
                        sa[0][3] <= sbox_out[sa[0][3]];
                        sa[1][0] <= sbox_out[sa[1][0]];
                        sa[1][1] <= sbox_out[sa[1][1]];
                        sa[1][2] <= sbox_out[sa[1][2]];
                        sa[1][3] <= sbox_out[sa[1][3]];
                        sa[2][0] <= sbox_out[sa[2][0]];
                        sa[2][1] <= sbox_out[sa[2][1]];
                        sa[2][2] <= sbox_out[sa[2][2]];
                        sa[2][3] <= sbox_out[sa[2][3]];
                        sa[3][0] <= sbox_out[sa[3][0]];
                        sa[3][1] <= sbox_out[sa[3][1]];
                        sa[3][2] <= sbox_out[sa[3][2]];
                        sa[3][3] <= sbox_out[sa[3][3]];
                        sa[0][0] ^= wo_0;
                        sa[1][1] ^= wo_1;
                        sa[2][2] ^= wo_2;
                        sa[3][3] ^= wo_3;
                        text_out <= {sa[3][3], sa[3][2], sa[3][1], sa[3][0], sa[2][3], sa[2][2], sa[2][1], sa[2][0], sa[1][3], sa[1][2], sa[1][1], sa[1][0], sa[0][3], sa[0][2], sa[0][1], sa[0][0]};
                        done <= 1'b1;
                    end
                end
                default: begin
                    dcnt <= dcnt - 1;
                end
            endcase
        end
    end

endmodule

