                        sa[1][1] <= sa[1][1] ^ sa[2][0] ^ sa[3][3] ^ sa[0][2];
                        sa[1][2] <= sa[1][2] ^ sa[2][1] ^ sa[3][0] ^ sa[0][1];
                        sa[1][3] <= sa[1][3] ^ sa[2][2] ^ sa[3][1] ^ sa[0][0];
