                        sa[2][2] <= sa[2][2] ^ sa[3][1] ^ sa[0][0] ^ sa[1][3];
                        sa[2][3] <= sa[2][3] ^ sa[3][2] ^ sa[0][3] ^ sa[1][2];
                        sa[3][0] <= sa[3][0] ^ sa[0][2] ^ sa[1][1] ^ sa[2][0];
                        sa[3][1] <= sa[3][1] ^ sa[0][1] ^ sa[1][0] ^ sa[2][3];
                        sa[3][2] <= sa[3][2] ^ sa[0][0] ^ sa[1][3] ^ sa[2][2];
                        sa[3][3] <= sa[3][3] ^ sa[0][3] ^ sa[1][2] ^ sa[2][1];
                    end
                    dcnt <= dcnt + 1;
                end
            end
        end
    end

    // Output generation
    always @(posedge clk) begin
        if (dcnt == 4'b1010) begin
            sa[0][0] <= sa[0][0] ^ wo_0;
            sa[1][0] <= sa[1][0] ^ wo_1;
            sa[2][0] <= sa[2][0] ^ wo_2;
            sa[3][0] <= sa[3][0] ^ wo_3;
        end else if (dcnt == 4'b1011) begin
            text_out[7:0] <= sa[3][0];
            text_out[15:8] <= sa[2][0];
            text_out[23:16] <= sa[1][0];
            text_out[31:24] <= sa[0][0];
            text_out[39:32] <= sa[3][1];
            text_out[47:40] <= sa[2][1];
            text_out[55:48] <= sa[1][1];
            text_out[63:56] <= sa[0][1];
            text_out[71:64] <= sa[3][2];
            text_out[79:72] <= sa[2][2];
            text_out[87:80] <= sa[1][2];
            text_out[95:88] <= sa[0][2];
            text_out[103:96] <= sa[3][3];
            text_out[111:104] <= sa[2][3];
            text_out[119:112] <= sa[1][3];
            text_out[127:120] <= sa[0][3];
            done <= 1'b1;
        end else begin
            done <= 1'b0;
        end
    end

endmodule

module aes_cipher_top (
    input clk,
    input rst,
    input ld,
    output done,
    input [127:0] key,
    input [127:0] text_in,
    output [127:0] text_out
);

    // Interface
    wire [127:0] text_in_r;
    wire [7:0] sa[0:3][0:3];
    wire [3:0] dcnt;
    wire ld_r;

    // Registers
    reg [127:0] text_in_r;
    reg [7:0] sa[0:3][0:3];
    reg [3:0] dcnt;
    reg ld_r;

    // Output
    assign done = (dcnt == 0);
    assign text_out = sa[0][0] ^ sa[1][1] ^ sa[2][2] ^ sa[3][3];

    // Data Loading
    always @(posedge clk) begin
        if (rst) begin
            text_in_r <= 0;
            ld_r <= 0;
        end else if (ld) begin
            text_in_r <= text_in;
            ld_r <= 1;
        end else begin
            ld_r <= 0;
        end
    end

    // Round Counter
    always @(posedge clk) begin
        if (rst) begin
            dcnt <= 11;
        end else if (ld) begin
            dcnt <= 11;
        end else if (dcnt > 0) begin
            dcnt <= dcnt - 1;
        end
    end

    // State Matrix
    aes_key_expand_128 key_expand(
        .clk(clk),
        .kld(ld_r),
        .key(key),
        .wo_0(sa[0][0]),
        .wo_1(sa[1][1]),
        .wo_2(sa[2][2]),
        .wo_3(sa[3][3])
    );

    // Round Operations
    aes_round_op round_op(
        .clk(clk),
        .rst(rst),
        .ld(ld_r),
        .dcnt(dcnt),
        .text_in_r(text_in_r),
        .sa(sa),
        .text_out(text_out)
    );

endmodule

