                        sa[2][1] <= sa[2][1] ^ kb[kcnt];
                        sa[2][2] <= sa[2][2] ^ kb[kcnt];
                        sa[2][3] <= sa[2][3] ^ kb[kcnt];
                        sa[3][0] <= sa[3][0] ^ kb[kcnt];
                        sa[3][1] <= sa[3][1] ^ kb[kcnt];
                        sa[3][2] <= sa[3][2] ^ kb[kcnt];
