                        sa[1][2] <= text_in[79:72];
                        sa[1][3] <= text_in[71:64];
                        sa[2][0] <= text_in[63:56];
                        sa[2][1] <= text_in[55:48];
                        sa[2][2] <= text_in[47:40];
