                end
            endcase
        end
    end

    // Key schedule loading
    always @(posedge clk) begin
        if (rst) begin
            kcnt <= 10;
        end else begin
            if (kld) begin
                kb[kcnt] <= wk3;
                kb[kcnt-1] <= wk2;
                kb[kcnt-2] <= wk1;
                kb[kcnt-3] <= wk0;
                kcnt <= kcnt - 4;
            end
        end
    end

    // Output generation
    always @(posedge clk) begin
        if (rst) begin
            text_out <= 0;
        end else begin
            if (dcnt == 0) begin
                text_out <= text_out;
            end else if (dcnt == 11) begin
                text_out <= sa_next[127:120] ^ sa_next[119:112] ^ sa_next[111:104] ^ sa_next[103:96] ^ sa_next[95:88] ^ sa_next[87:80] ^ sa_next[79:72] ^ sa_next[71:64] ^ sa_next[63:56] ^ sa_next[55:48] ^ sa_next[47:40] ^ sa_next[39:32] ^ sa_next[31:24] ^ sa_next[23:16] ^ sa_next[15:8] ^ sa_next[7:0];
            end
        end
    end

    // Completion signal
    assign done = (dcnt == 11);

endmodule

