module aes_key_expand_128(
    input clk,
    input kld,
    input [127:0] key,
    output reg [31:0] wo_0,
    output reg [31:0] wo_1,
    output reg [31:0] wo_2,
    output reg [31:0] wo_3
);

reg [31:0] w0, w1, w2, w3;
reg [7:0] rcon;

aes_rcon rcon_gen(
    .clk(clk),
    .kld(kld),
    .out(rcon)
);

aes_sbox sbox0(
    .a(key[127:120]),
    .b(w0[31:24])
);

aes_sbox sbox1(
    .a(key[119:112]),
    .b(w0[23:16])
);

aes_sbox sbox2(
    .a(key[111:104]),
    .b(w0[15:8])
);

aes_sbox sbox3(
    .a(key[103:96]),
    .b(w0[7:0])
);

always @(posedge clk) begin
    if (kld) begin
        w0 <= key[127:96];
        w1 <= key[95:64];
        w2 <= key[63:32];
        w3 <= key[31:0];
        wo_0 <= w0;
        wo_1 <= w1;
        wo_2 <= w2;
        wo_3 <= w3;
    end else begin
        w0 <= w3 ^ (sbox0 ^ sbox1 ^ sbox2 ^ sbox3 ^ rcon);
        w1 <= w0 ^ w1;
        w2 <= w1 ^ w2;
        w3 <= w2 ^ w3;
        wo_0 <= w0;
        wo_1 <= w1;
        wo_2 <= w2;
        wo_3 <= w3;
    end
end

endmodule

