always @(posedge clk) begin
    if (kld) begin
        rcnt <= 4'h1;
        out <= 32'h01_00_00_00;
    end else begin
        if (rcnt == 4'h9) begin
            rcnt <= 4'h0;
            out <= 32'h00_00_00_00;
