module sd_bd (
    input clk,
    input rst,
    input we_m,
    input [RAM_MEM_WIDTH-1:0] dat_in_m,
    output reg [BD_WIDTH-1:0] free_bd,
    input re_s,
    output reg ack_o_s,
    input a_cmp,
    output reg [RAM_MEM_WIDTH-1:0] dat_out_s
);

    parameter BD_SIZE = 128;
    parameter BD_WIDTH = $clog2(BD_SIZE);
    parameter RAM_MEM_WIDTH = 32;
    parameter RAM_MEM_WIDTH_16 = 16;

    reg [BD_WIDTH-1:0] m_wr_pnt;
    reg [BD_WIDTH-1:0] s_rd_pnt;
    reg [BD_WIDTH-1:0] m_wr_cnt;
    reg [BD_WIDTH-1:0] s_rd_cnt;
    reg [BD_WIDTH-1:0] new_bw;
    reg [BD_WIDTH-1:0] last_a_cmp;
    reg [BD_WIDTH-1:0] free_bd_reg;
    reg [RAM_MEM_WIDTH-1:0] bd_mem [BD_SIZE-1:0];

    always @(posedge clk) begin
        if (rst) begin
            m_wr_pnt <= 0;
            s_rd_pnt <= 0;
            m_wr_cnt <= 0;
            s_rd_cnt <= 0;
            new_bw <= 0;
            last_a_cmp <= 0;
            free_bd_reg <= BD_SIZE / 2;
        end else begin
            if (we_m) begin
                if (free_bd_reg > 0) begin
                    bd_mem[m_wr_pnt] <= dat_in_m;
                    m_wr_pnt <= m_wr_pnt + 1;
                    m_wr_cnt <= m_wr_cnt + 1;
                    if (m_wr_cnt == RAM_MEM_WIDTH_16) begin
                        m_wr_cnt <= 0;
                        new_bw <= 1;
                    end
                end
            end
            if (re_s) begin
                if (s_rd_cnt < RAM_MEM_WIDTH_16) begin
                    dat_out_s <= bd_mem[s_rd_pnt];
                    s_rd_pnt <= s_rd_pnt + 1;
                    s_rd_cnt <= s_rd_cnt + 1;
                    ack_o_s <= 1;
                end else begin
                    ack_o_s <= 0;
                end
            end
            if (a_cmp) begin
                if (last_a_cmp == 0) begin
                    free_bd_reg <= free_bd_reg + 1;
                end
                last_a_cmp <= a_cmp;
            end
            if (new_bw) begin
                free_bd_reg <= free_bd_reg - 1;
                new_bw <= 0;
            end
        end
    end

    assign free_bd = free_bd_reg;

endmodule

