    reg [BD_WIDTH-1:0] new_bw;
    reg [BD_WIDTH-1:0] last_a_cmp;

    reg [RAM_MEM_WIDTH-1:0] bd_mem [BD_SIZE-1:0];

    assign free_bd = free_bd_reg;

    always @(posedge clk) begin
        if (rst) begin
            m_wr_pnt <= 0;
            s_rd_pnt <= 0;
            m_wr_cnt <= 0;
            s_rd_cnt <= 0;
            new_bw <= 0;
            last_a_cmp <= 0;
            free_bd_reg <= BD_SIZE >> 1;
        end else begin
            if (we_m) begin
                if (free_bd_reg > 0) begin
                    bd_mem[m_wr_pnt] <= dat_in_m;
                    m_wr_pnt <= m_wr_pnt + 1;
                    m_wr_cnt <= m_wr_cnt + 1;
                    if (m_wr_cnt == 1) begin
                        new_bw <= 1;
                        free_bd_reg <= free_bd_reg - 1;
                    end else if (m_wr_cnt == 2) begin
                        new_bw <= 0;
                        m_wr_cnt <= 0;
                    end
                end
            end
            if (re_s) begin
                if (s_rd_cnt == 0) begin
                    ack_o_s <= 1;
                end
                if (s_rd_cnt == 1) begin
                    ack_o_s <= 0;
                end
                if (s_rd_cnt < 3) begin
                    dat_out_s <= bd_mem[s_rd_pnt];
                    s_rd_pnt <= s_rd_pnt + 1;
                    s_rd_cnt <= s_rd_cnt + 1;
                end else begin
                    s_rd_cnt <= 0;
                end
            end
            if (a_cmp) begin
                if (last_a_cmp == 0) begin
                    free_bd_reg <= free_bd_reg + 1;
                end
                last_a_cmp <= a_cmp;
            end
        end
    end

endmodule

