module sd_bd (
    input clk,
    input rst,
    input we_m,
    input [RAM_MEM_WIDTH-1:0] dat_in_m,
    output [BD_WIDTH-1:0] free_bd,
    input re_s,
    output ack_o_s,
    input a_cmp,
    output [RAM_MEM_WIDTH-1:0] dat_out_s
);

    parameter BD_SIZE = 128;
    parameter BD_WIDTH = $clog2(BD_SIZE);
    parameter RAM_MEM_WIDTH = 32;
    parameter BIG_ENDIAN = 1;
    parameter LITTLE_ENDIAN = 0;

    reg [BD_WIDTH-1:0] free_bd_reg;
    reg [BD_WIDTH-1:0] m_wr_pnt;
    reg [BD_WIDTH-1:0] s_rd_pnt;
    reg [BD_WIDTH-1:0] m_wr_cnt;
    reg [BD_WIDTH-1:0] s_rd_cnt;
    reg new_bw;
    reg last_a_cmp;

    reg [RAM_MEM_WIDTH-1:0] bd_mem [BD_SIZE-1:0];

    assign free_bd = free_bd_reg;

    always @(posedge clk) begin
        if (rst) begin
            free_bd_reg <= BD_SIZE / 2;
            m_wr_pnt <= 0;
            s_rd_pnt <= 0;
            m_wr_cnt <= 0;
            s_rd_cnt <= 0;
            new_bw <= 0;
            last_a_cmp <= 0;
        end else begin
            if (we_m) begin
                if (free_bd_reg > 0) begin
                    bd_mem[m_wr_pnt] <= dat_in_m;
                    m_wr_pnt <= m_wr_pnt + 1;
                    m_wr_cnt <= m_wr_cnt + 1;
                    if (m_wr_cnt == 1) begin
                        new_bw <= 1;
                    end else if (m_wr_cnt == 2) begin
                        new_bw <= 0;
                        free_bd_reg <= free_bd_reg - 1;
                    end
                end
            end
            if (re_s) begin
                if (s_rd_cnt == 0) begin
                    dat_out_s <= bd_mem[s_rd_pnt];
                    s_rd_pnt <= s_rd_pnt + 1;
                    s_rd_cnt <= s_rd_cnt + 1;
                end else if (s_rd_cnt == 1) begin
                    dat_out_s <= bd_mem[s_rd_pnt];
                    s_rd_pnt <= s_rd_pnt + 1;
                    s_rd_cnt <= s_rd_cnt + 1;
                end else if (s_rd_cnt == 2) begin
                    dat_out_s <= bd_mem[s_rd_pnt];
                    s_rd_pnt <= s_rd_pnt + 1;
                    s_rd_cnt <= s_rd_cnt + 1;
                end else if (s_rd_cnt == 3) begin
                    dat_out_s <= bd_mem[s_rd_pnt];
                    s_rd_pnt <= s_rd_pnt + 1;
                    s_rd_cnt <= 0;
                    ack_o_s <= 1;
                end
            end
            if (a_cmp) begin
                if (!last_a_cmp) begin
                    free_bd_reg <= free_bd_reg + 1;
                end
                last_a_cmp <= 1;
            end else begin
                last_a_cmp <= 0;
            end
        end
    end

endmodule

module sd_bd (
    input clk,
    input rst,
    input we_m,
    input [RAM_MEM_WIDTH-1:0] dat_in_m,
    output [BD_WIDTH-1:0] free_bd,
    input re_s,
    output ack_o_s,
    input a_cmp,
    output [RAM_MEM_WIDTH-1:0] dat_out_s
);

    // Define constants
    parameter BD_SIZE = 64;
    parameter BD_WIDTH = $clog2(BD_SIZE);
    parameter RAM_MEM_WIDTH = 32;
    parameter RAM_MEM_WIDTH_16 = 16;

    // Define internal signals
    reg [BD_WIDTH-1:0] free_bd_reg;
    reg [BD_WIDTH-1:0] free_bd_next;
    reg [BD_WIDTH-1:0] m_wr_pnt;
    reg [BD_WIDTH-1:0] s_rd_pnt;
    reg [BD_WIDTH-1:0] m_wr_cnt;
    reg [BD_WIDTH-1:0] s_rd_cnt;
    reg [RAM_MEM_WIDTH-1:0] bd_mem [BD_SIZE-1:0];
    reg [RAM_MEM_WIDTH-1:0] dat_out_s_reg;
    reg ack_o_s_reg;
    reg [BD_WIDTH-1:0] new_bw;
    reg [BD_WIDTH-1:0] last_a_cmp;
    reg [BD_WIDTH-1:0] last_a_cmp_reg;
    reg [BD_WIDTH-1:0] last_a_cmp_next;

    // Define internal variables
    integer i;

    // Initialize internal signals
    initial begin
        free_bd_reg = 0;
        m_wr_pnt = 0;
        s_rd_pnt = 0;
        m_wr_cnt = 0;
        s_rd_cnt = 0;
        dat_out_s_reg = 0;
        ack_o_s_reg = 0;
        new_bw = 0;
        last_a_cmp = 0;
        last_a_cmp_reg = 0;
        last_a_cmp_next = 0;
    end

    // Update internal signals
    always @(posedge clk) begin
        if (rst) begin
            free_bd_reg <= 0;
            m_wr_pnt <= 0;
            s_rd_pnt <= 0;
            m_wr_cnt <= 0;
            s_rd_cnt <= 0;
            dat_out_s_reg <= 0;
            ack_o_s_reg <= 0;
            new_bw <= 0;
            last_a_cmp <= 0;
            last_a_cmp_reg <= 0;
            last_a_cmp_next <= 0;
        end else begin
            free_bd_reg <= free_bd_next;
            dat_out_s_reg <= bd_mem[s_rd_pnt];
            ack_o_s_reg <= (s_rd_cnt == 0) ? 0 : 1;
            last_a_cmp_reg <= last_a_cmp;
            last_a_cmp_next <= (a_cmp) ? 0 : (last_a_cmp_reg + 1);
        end
    end

    // Update free_bd
    always @(*) begin
        free_bd_next = free_bd_reg;
        if (we_m && (free_bd_reg > 0)) begin
            free_bd_next = free_bd_reg - 1;
        end
        if (re_s && (s_rd_cnt > 0)) begin
            free_bd_next = free_bd_reg + 1;
        end
        if (a_cmp && (last_a_cmp_reg > 0)) begin
            free_bd_next = free_bd_reg + 1;
        end
    end

    // Write BD
    always @(posedge clk) begin
        if (we_m && (free_bd_reg > 0)) begin
            bd_mem[m_wr_pnt] <= dat_in_m;
            m_wr_pnt <= (m_wr_pnt == BD_SIZE-1) ? 0 : m_wr_pnt + 1;
            m_wr_cnt <= (m_wr_cnt == 0) ? 1 : m_wr_cnt + 1;
            new_bw <= (m_wr_cnt == 2) ? 1 : 0;
        end
    end

    // Read BD
    always @(posedge clk) begin
        if (re_s && (s_rd_cnt > 0)) begin
            dat_out_s_reg <= bd_mem[s_rd_pnt];
            s_rd_pnt <= (s_rd_pnt == BD_SIZE-1) ? 0 : s_rd_pnt + 1;
            s_rd_cnt <= (s_rd_cnt == 0) ? 1 : s_rd_cnt + 1;
        end
    end

    // Release BD
    always @(posedge clk) begin
        if (a_cmp && (last_a_cmp_reg > 0)) begin
            free_bd_reg <= free_bd_reg + 1;
            last_a_cmp_reg <= last_a_cmp_next;
        end
    end

    // Assign outputs
    assign free_bd = free_bd_reg;
    assign ack_o_s = ack_o_s_reg;
    assign dat_out_s = dat_out_s_reg;

endmodule

