module sd_crc_16(
    input BITVAL,
    input Enable,
    input CLK,
    input RST,
    output reg [15:0] CRC
);

    always @(posedge CLK or posedge RST) begin
        if (RST) begin
            CRC <= 16'h0000;
        end else if (Enable) begin
            CRC <= {CRC[14:0], BITVAL ^ CRC[15]};
            CRC[12] <= CRC[11] ^ CRC[15];
            CRC[5] <= CRC[4] ^ CRC[15];
        end
    end

endmodule

module sd_crc_16 (
    input BITVAL,
    input Enable,
    input CLK,
    input RST,
    output reg [15:0] CRC
);

    reg [15:0] crc_reg;
    reg inv;

    always @(posedge CLK or posedge RST) begin
        if (RST) begin
            crc_reg <= 16'h0000;
        end else if (Enable) begin
            crc_reg <= {crc_reg[14:0], crc_reg[15] ^ BITVAL};
            crc_reg[12] <= crc_reg[11] ^ inv;
            crc_reg[5] <= crc_reg[4] ^ inv;
            crc_reg[0] <= inv;
        end
    end

    assign inv = crc_reg[15] ^ BITVAL;

    assign CRC = crc_reg;

endmodule

