module sd_crc_16 (
    input BITVAL,
    input Enable,
    input CLK,
    input RST,
    output reg [15:0] CRC
);

    // Combinational Logic
    wire inv = BITVAL ^ CRC[15];

    // Sequential Logic
    always @(posedge CLK or posedge RST) begin
        if (RST) begin
            CRC <= 16'h0000;
        end else if (Enable) begin
            CRC <= {CRC[14:0], inv};
            CRC[12] <= CRC[11] ^ inv;
            CRC[5] <= CRC[4] ^ inv;
        end
    end

endmodule

module sd_crc_16 (
    input BITVAL,
    input Enable,
    input CLK,
    input RST,
    output reg [15:0] CRC
);

    reg [15:0] crc_reg;
    reg [15:0] crc_next;
    reg inv;

    always @(posedge CLK or posedge RST) begin
        if (RST) begin
            crc_reg <= 16'h0000;
        end else if (Enable) begin
            crc_reg <= crc_next;
        end
    end

    always @(*) begin
        inv = BITVAL ^ crc_reg[15];
        crc_next = {crc_reg[14:0], 1'b0} ^ ({1'b0, inv} & 16'h8005);
    end

    assign CRC = crc_reg;

endmodule

