module sd_crc_7 (
    input BITVAL,
    input Enable,
    input CLK,
    input RST,
    output reg [6:0] CRC
);

    reg inv;

    always @(posedge CLK or posedge RST) begin
        if (RST) begin
            CRC <= 7'b0;
        end else if (Enable) begin
            inv <= BITVAL ^ CRC[6];
            CRC[0] <= inv;
            CRC[3] <= CRC[2] ^ inv;
            CRC[6:1] <= CRC[5:0];
        end
    end

endmodule

