module sd_data_serial_host (
    input sd_clk,
    input rst,
    input [31:0] data_in,
    input [1:0] start_dat,
    input ack_transfer,
    input [SD_BUS_W-1:0] DAT_dat_i,
    output reg rd,
    output reg [SD_BUS_W-1:0] data_out,
    output reg we,
    output reg DAT_oe_o,
    output reg [SD_BUS_W-1:0] DAT_dat_o,
    output reg busy_n,
    output reg transm_complete,
    output reg crc_ok
);

    // Internal signals
    reg [SD_BUS_W-1:0] crc_in;
    reg crc_en;
    reg crc_rst;
    wire [SD_BUS_W-1:0] crc_out;
    reg [SD_BUS_W-1:0] temp_in;
    reg [10:0] transf_cnt;
    reg [5:0] state;
    reg [5:0] next_state;
    reg [2:0] crc_status;
    reg busy_int;
    reg ack_transfer_int;
    reg ack_q;
    reg q_start_bit;
    reg [4:0] crc_c;
    reg [3:0] last_din;
    reg [2:0] crc_s;
    reg [31:0] write_buf_0;
    reg [31:0] write_buf_1;
    reg [31:0] sd_data_out;
    reg [0:0] out_buff_ptr;
    reg [0:0] in_buff_ptr;
    reg [2:0] data_send_index;

    // CRC Generation Block
    sd_crc_16 crc_gen (
        .BITVAL(crc_in),
        .Enable(crc_en),
        .CLK(sd_clk),
        .RST(crc_rst),
        .CRC(crc_out)
    );

    // Acknowledgment Synchronization Block
    always @(posedge sd_clk) begin
        if (rst) begin
            ack_q <= 1'b0;
            ack_transfer_int <= 1'b0;
        end else begin
            ack_q <= ack_transfer;
            ack_transfer_int <= ack_q;
        end
    end

