                end else if (start_dat == 2'b10) begin
                    next_state = READ_WAIT;
                end else begin
                    next_state = IDLE;
                end
            end
            WRITE_DAT: begin
                if (transf_cnt == SD_BUS_W) begin
                    next_state = WRITE_CRC;
                end else begin
                    next_state = WRITE_DAT;
                end
            end
            WRITE_CRC: begin
                if (crc_status == OK) begin
                    next_state = WRITE_BUSY;
                end else begin
                    next_state = WRITE_CRC;
                end
            end
            WRITE_BUSY: begin
                if (busy_int == 0) begin
                    next_state = IDLE;
                end else begin
                    next_state = WRITE_BUSY;
                end
            end
            READ_WAIT: begin
                if (DAT_dat_i[0] == 0) begin
                    next_state = READ_DAT;
                end else begin
                    next_state = READ_WAIT;
