    end

    // Start Bit Synchronization Block
    always @(posedge sd_clk) begin
        if (rst) begin
            q_start_bit <= 1'b1;
        end else begin
            if (!DAT_dat_i[0] & state == READ_WAIT) begin
                q_start_bit <= 1'b0;
            end else begin
                q_start_bit <= 1'b1;
