module sd_data_serial_host (
    input sd_clk,
    input rst,
    input [31:0] data_in,
    input [1:0] start_dat,
    input ack_transfer,
    input [SD_BUS_W-1:0] DAT_dat_i,
    output rd,
    output [SD_BUS_W-1:0] data_out,
    output we,
    output DAT_oe_o,
    output [SD_BUS_W-1:0] DAT_dat_o,
    output busy_n,
    output transm_complete,
    output crc_ok
);

    // Internal signals
    reg [SD_BUS_W-1:0] crc_in;
    reg crc_en;
    reg crc_rst;
    wire [SD_BUS_W-1:0] crc_out;
    reg [SD_BUS_W-1:0] temp_in;
    reg [10:0] transf_cnt;
    reg [5:0] state;
    reg [5:0] next_state;
    reg [2:0] crc_status;
    reg busy_int;
    reg ack_transfer_int;
    reg ack_q;
    reg q_start_bit;
    reg [4:0] crc_c;
    reg [3:0] last_din;
    reg [2:0] crc_s;
    reg [31:0] write_buf_0;
    reg [31:0] write_buf_1;
    reg [31:0] sd_data_out;
    reg [0:0] out_buff_ptr;
    reg [0:0] in_buff_ptr;
    reg [2:0] data_send_index;

    // CRC Generation Block
    sd_crc_16 crc_16_gen (
        .BITVAL(crc_in),
        .Enable(crc_en),
        .CLK(sd_clk),
        .RST(crc_rst),
        .CRC(crc_out)
    );

    // Acknowledgment Synchronization Block
    always @(posedge sd_clk) begin
        if (rst) begin
            ack_q <= 0;
            ack_transfer_int <= 0;
        end else begin
            ack_q <= ack_transfer;
            ack_transfer_int <= ack_q;
        end
    end

    // Finite State Machine Logic for Transmit and Receive Operations
    always @(*) begin
        case (state)
            IDLE: begin
                if (start_dat == 2'b01) begin
                    next_state = WRITE_DAT;
                end else if (start_dat == 2'b10) begin
                    next_state = READ_WAIT;
                end else begin
                    next_state = IDLE;
                end
            end
            WRITE_DAT: begin
                if (transf_cnt == BIT_BLOCK) begin
                    next_state = WRITE_CRC;
                end else begin
                    next_state = WRITE_DAT;
                end
            end
            WRITE_CRC: begin
                if (crc_status == OK) begin
                    next_state = WRITE_BUSY;
                end else begin
                    next_state = WRITE_CRC;
                end
            end
            WRITE_BUSY: begin
                if (busy_int == 0) begin
                    next_state = IDLE;
                end else begin
                    next_state = WRITE_BUSY;
                end
            end
            READ_WAIT: begin
                if (DAT_dat_i[0] == 0) begin
                    next_state = READ_DAT;
                end else begin
                    next_state = READ_WAIT;
                end
            end
            READ_DAT: begin
                if (transf_cnt == BIT_BLOCK_REC) begin
                    next_state = IDLE;
                end else begin
                    next_state = READ_DAT;
                end
            end
            default: begin
                next_state = IDLE;
            end
        endcase
    end

    // Start Bit Synchronization Block
    always @(posedge sd_clk) begin
        if (rst) begin
            q_start_bit <= 1;
        end else begin
            if (!DAT_dat_i[0] & (state == READ_WAIT)) begin
                q_start_bit <= 0;
            end else begin
                q_start_bit <= 1;
            end
        end
    end

    // Sequential Logic for FSM
    always @(posedge sd_clk) begin
        if (rst) begin
            state <= IDLE;
        end else begin
            state <= next_state;
        end
    end

    // Output Logic for FSM Control
    always @(posedge sd_clk) begin
        if (rst) begin
            write_buf_0 <= 0;
            write_buf_1 <= 0;
            DAT_oe_o <= 0;
            crc_en <= 0;
            crc_rst <= 1;
            transf_cnt <= 0;
            rd <= 0;
            last_din <= 0;
            crc_c <= 0;
            DAT_dat_o <= 0;
            crc_status <= 7;
            crc_s <= 0;
            transm_complete <= 0;
            busy_n <= 1;
            we <= 0;
            data_out <= 0;
            crc_ok <= 0;
            busy_int <= 0;
            data_send_index <= 0;
            out_buff_ptr <= 0;
            in_buff_ptr <= 0;
        end else begin
            case (state)
                IDLE: begin
                    crc_en <= 0;
                    crc_rst <= 1;
                    transf_cnt <= 0;
                    rd <= 0;
                    last_din <= 0;
                    crc_c <= 0;
                    DAT_dat_o <= 0;
                    crc_status <= 7;
                    crc_s <= 0;
                    transm_complete <= 0;
                    busy_n <= 1;
                    we <= 0;
                    data_out <= 0;
                    crc_ok <= 0;
                    busy_int <= 0;
                    data_send_index <= 0;
                    out_buff_ptr <= 0;
                    in_buff_ptr <= 0;
                    if (start_dat == 2'b01) begin
                        write_buf_0 <= data_in;
                        write_buf_1 <= data_in;
                        DAT_oe_o <= 1;
                        crc_en <= 1;
                        crc_rst <= 0;
                        transf_cnt <= 0;
                        rd <= 1;
                        last_din <= data_in[3:0];
                        crc_c <= 0;
                        DAT_dat_o <= data_in[3:0];
                        crc_status <= 7;
                        crc_s <= 0;
                        transm_complete <= 0;
                        busy_n <= 0;
                        we <= 1;
                        data_out <= 0;
                        crc_ok <= 0;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end else if (start_dat == 2'b10) begin
                        DAT_oe_o <= 0;
                        crc_en <= 0;
                        crc_rst <= 1;
                        transf_cnt <= 0;
                        rd <= 0;
                        last_din <= 0;
                        crc_c <= 0;
                        DAT_dat_o <= 0;
                        crc_status <= 7;
                        crc_s <= 0;
                        transm_complete <= 0;
                        busy_n <= 1;
                        we <= 0;
                        data_out <= 0;
                        crc_ok <= 0;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end
                end
                WRITE_DAT: begin
                    crc_en <= 1;
                    crc_rst <= 0;
                    transf_cnt <= transf_cnt + 1;
                    rd <= 1;
                    last_din <= sd_data_out[3:0];
                    crc_c <= crc_c + 1;
                    DAT_dat_o <= sd_data_out[3:0];
                    crc_status <= 7;
                    crc_s <= 0;
                    transm_complete <= 0;
                    busy_n <= 0;
                    we <= 1;
                    data_out <= 0;
                    crc_ok <= 0;
                    busy_int <= 0;
                    data_send_index <= data_send_index + 1;
                    out_buff_ptr <= out_buff_ptr;
                    in_buff_ptr <= in_buff_ptr;
                    if (transf_cnt == BIT_BLOCK) begin
                        crc_en <= 0;
                        crc_rst <= 1;
                        transf_cnt <= 0;
                        rd <= 0;
                        last_din <= 0;
                        crc_c <= 0;
                        DAT_dat_o <= 0;
                        crc_status <= 7;
                        crc_s <= 0;
                        transm_complete <= 0;
                        busy_n <= 1;
                        we <= 0;
                        data_out <= 0;
                        crc_ok <= 0;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end
                end
                WRITE_CRC: begin
                    crc_en <= 0;
                    crc_rst <= 1;
                    transf_cnt <= 0;
                    rd <= 0;
                    last_din <= 0;
                    crc_c <= 0;
                    DAT_dat_o <= 0;
                    crc_status <= 7;
                    crc_s <= 0;
                    transm_complete <= 0;
                    busy_n <= 1;
                    we <= 0;
                    data_out <= 0;
                    crc_ok <= 0;
                    busy_int <= 0;
                    data_send_index <= 0;
                    out_buff_ptr <= 0;
                    in_buff_ptr <= 0;
                    if (crc_status == OK) begin
                        crc_en <= 0;
                        crc_rst <= 1;
                        transf_cnt <= 0;
                        rd <= 0;
                        last_din <= 0;
                        crc_c <= 0;
                        DAT_dat_o <= 0;
                        crc_status <= 7;
                        crc_s <= 0;
                        transm_complete <= 1;
                        busy_n <= 1;
                        we <= 0;
                        data_out <= 0;
                        crc_ok <= 1;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end
                end
                WRITE_BUSY: begin
                    crc_en <= 0;
                    crc_rst <= 1;
                    transf_cnt <= 0;
                    rd <= 0;
                    last_din <= 0;
                    crc_c <= 0;
                    DAT_dat_o <= 0;
                    crc_status <= 7;
                    crc_s <= 0;
                    transm_complete <= 0;
                    busy_n <= 1;
                    we <= 0;
                    data_out <= 0;
                    crc_ok <= 0;
                    busy_int <= 0;
                    data_send_index <= 0;
                    out_buff_ptr <= 0;
                    in_buff_ptr <= 0;
                    if (busy_int == 0) begin
                        crc_en <= 0;
                        crc_rst <= 1;
                        transf_cnt <= 0;
                        rd <= 0;
                        last_din <= 0;
                        crc_c <= 0;
                        DAT_dat_o <= 0;
                        crc_status <= 7;
                        crc_s <= 0;
                        transm_complete <= 1;
                        busy_n <= 1;
                        we <= 0;
                        data_out <= 0;
                        crc_ok <= 1;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end
                end
                READ_WAIT: begin
                    crc_en <= 0;
                    crc_rst <= 1;
                    transf_cnt <= 0;
                    rd <= 0;
                    last_din <= 0;
                    crc_c <= 0;
                    DAT_dat_o <= 0;
