                        busy_n <= 0;
                        we <= 1;
                        data_out <= 0;
                        crc_ok <= 0;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end
                end
                READ_DAT: begin
                    crc_en <= 1;
                    crc_rst <= 0;
                    transf_cnt <= transf_cnt + 1;
                    rd <= 1;
                    last_din <= DAT_dat_i[3:0];
                    crc_c <= crc_c + 1;
