                        crc_en <= 0;
                        crc_rst <= 1;
                        transf_cnt <= 0;
                        rd <= 0;
                        last_din <= 0;
                        crc_c <= 0;
                        DAT_dat_o <= 0;
                        crc_status <= 7;
                        crc_s <= 0;
                        transm_complete <= 1;
                        busy_n <= 1;
                        we <= 0;
                        data_out <= 0;
                        crc_ok <= 1;
                        busy_int <= 0;
                        data_send_index <= 0;
                        out_buff_ptr <= 0;
                        in_buff_ptr <= 0;
                    end
                end
                default: begin
                    crc_en <= 0;
                    crc_rst <= 1;
                    transf_cnt <= 0;
                    rd <= 0;
                    last_din <= 0;
                    crc_c <= 0;
                    DAT_dat_o <= 0;
