module sd_fifo_rx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    output reg [31:0] m_wb_dat_o,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [SD_BUS_W-1:0] dat_i,
    input wr,
    output full,
    output empty
);

parameter MEM_OFFSET = 4;
parameter FIFO_RX_MEM_DEPTH = 1024;
parameter FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

reg [FIFO_RX_MEM_ADR_SIZE-1:0] offset;
reg wb_free;
reg reset_rx_fifo;
reg rd;

sd_rx_fifo #(
    .BD_SIZE(FIFO_RX_MEM_DEPTH),
    .BD_WIDTH($clog2(FIFO_RX_MEM_DEPTH)),
    .MEM_ADR_SIZE(FIFO_RX_MEM_ADR_SIZE)
) rx_fifo (
    .d(dat_i),
    .wr(wr),
    .wclk(sd_clk),
    .q(m_wb_dat_o),
    .rd(rd),
    .full(full),
    .empty(empty),
    .mem_empt(),
    .rclk(clk),
    .rst(reset_rx_fifo)
);

always @(posedge clk) begin
    if (rst) begin
        m_wb_adr_o <= 0;
        m_wb_we_o <= 0;
        m_wb_dat_o <= 0;
        m_wb_cyc_o <= 0;
        m_wb_stb_o <= 0;
        m_wb_cti_o <= 0;
        m_wb_bte_o <= 0;
        offset <= 0;
        wb_free <= 1;
        reset_rx_fifo <= 1;
        rd <= 0;
    end else begin
        if (en) begin
            if (empty) begin
                rd <= 0;
            end else if (wb_free) begin
                rd <= 1;
                m_wb_adr_o <= adr + offset;
                m_wb_we_o <= 1;
                m_wb_dat_o <= rx_fifo.q;
                m_wb_cyc_o <= 1;
                m_wb_stb_o <= 1;
                m_wb_cti_o <= 0;
                m_wb_bte_o <= 0;
                wb_free <= 0;
            end
        end else begin
            reset_rx_fifo <= 1;
            rd <= 0;
            offset <= 0;
            wb_free <= 1;
            m_wb_adr_o <= 0;
            m_wb_we_o <= 0;
            m_wb_dat_o <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;
        end
    end
end

always @(posedge clk) begin
    if (rst) begin
        offset <= 0;
    end else begin
        if (en && m_wb_ack_i) begin
            offset <= offset + MEM_OFFSET;
        end
    end
end

always @(posedge clk) begin
    if (rst) begin
        reset_rx_fifo <= 1;
    end else begin
        if (en) begin
            reset_rx_fifo <= 0;
        end else begin
            reset_rx_fifo <= 1;
        end
    end
end

always @(posedge clk) begin
    if (rst) begin
        m_wb_cyc_o <= 0;
        m_wb_stb_o <= 0;
        m_wb_cti_o <= 0;
        m_wb_bte_o <= 0;
    end else begin
        if (en && m_wb_ack_i) begin
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;
        end else if (en && !m_wb_ack_i) begin
            m_wb_cyc_o <= 1;
            m_wb_stb_o <= 1;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;
        end
    end
end

endmodule

