module sd_fifo_rx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    output reg [31:0] m_wb_dat_o,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [SD_BUS_W-1:0] dat_i,
    input wr,
    output full,
    output empty
);

    // Constants
    localparam MEM_OFFSET = 4;
    localparam FIFO_RX_MEM_DEPTH = 1024;
    localparam FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

    // Registers
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] offset;
    reg wb_free;
    reg reset_rx_fifo;
    reg rd;

    // Wishbone signals
    assign m_wb_adr_o = adr + offset;
    assign m_wb_we_o = 1'b1;
    assign m_wb_dat_o = dat_i;
    assign m_wb_cyc_o = wb_free & en;
    assign m_wb_stb_o = wb_free & en;
    assign m_wb_cti_o = 3'b000;
    assign m_wb_bte_o = 2'b00;

    // RX FIFO signals
    wire [31:0] q;
    wire full;
    wire empty;
    wire [1:0] mem_empt;

    sd_rx_fifo rx_fifo (
        .d(dat_i),
        .wr(wr),
        .wclk(sd_clk),
        .q(q),
        .rd(rd),
        .full(full),
        .empty(empty),
        .mem_empt(mem_empt),
        .rclk(clk),
        .rst(reset_rx_fifo)
    );

    // State machine
    reg [1:0] state;
    parameter IDLE = 2'b00;
    parameter WRITE = 2'b01;
    parameter READ = 2'b10;

    always @(posedge clk) begin
        if (rst) begin
            state <= IDLE;
            offset <= 0;
            wb_free <= 1'b1;
            reset_rx_fifo <= 1'b1;
            rd <= 1'b0;
        end else begin
            case (state)
                IDLE: begin
                    if (en) begin
                        state <= WRITE;
                        reset_rx_fifo <= 1'b0;
                        rd <= 1'b0;
                    end
                end
                WRITE: begin
                    if (full) begin
                        state <= IDLE;
                        reset_rx_fifo <= 1'b1;
                        rd <= 1'b0;
                    end else if (wb_free) begin
                        state <= READ;
                        rd <= 1'b1;
                    end
                end
                READ: begin
                    if (empty) begin
                        state <= WRITE;
                        rd <= 1'b0;
                    end else if (m_wb_ack_i) begin
                        state <= WRITE;
                        offset <= offset + MEM_OFFSET;
                        rd <= 1'b0;
                        wb_free <= 1'b1;
                    end
                end
            endcase
        end
    end

endmodule

module sd_fifo_rx_filler (
    input clk,
    input rst,
    output [31:0] m_wb_adr_o,
    output m_wb_we_o,
    output [31:0] m_wb_dat_o,
    output m_wb_cyc_o,
    output m_wb_stb_o,
    input m_wb_ack_i,
    output [2:0] m_wb_cti_o,
    output [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [SD_BUS_W-1:0] dat_i,
    input wr,
    output full,
    output empty
);

    // Constants
    localparam MEM_OFFSET = 4;
    localparam FIFO_RX_MEM_DEPTH = 16;
    localparam FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

    // Registers
    reg [8:0] offset;
    reg wb_free;
    reg reset_rx_fifo;
    reg rd;

    // Wishbone signals
    assign m_wb_adr_o = adr + offset;
    assign m_wb_we_o = 1'b1;
    assign m_wb_dat_o = dat_i;
    assign m_wb_cyc_o = wb_free & en;
    assign m_wb_stb_o = wb_free & en;
    assign m_wb_cti_o = 3'b000;
    assign m_wb_bte_o = 2'b00;

    // RX FIFO signals
    wire [31:0] q;
    wire full;
    wire empty;
    wire [1:0] mem_empt;

    sd_rx_fifo rx_fifo (
        .d(dat_i),
        .wr(wr),
        .wclk(sd_clk),
        .q(q),
        .rd(rd),
        .full(full),
        .empty(empty),
        .mem_empt(mem_empt),
        .rclk(clk),
        .rst(reset_rx_fifo)
    );

    // State machine
    reg [1:0] state;
    localparam IDLE = 2'b00;
    localparam READ = 2'b01;
    localparam WRITE = 2'b10;

    always @(posedge clk) begin
        if (rst) begin
            // Reset all control signals and counters
            offset <= 0;
            wb_free <= 1'b1;
            reset_rx_fifo <= 1'b1;
            rd <= 1'b0;
            state <= IDLE;
        end else begin
            case (state)
                IDLE: begin
                    // Reset RX FIFO buffer
                    reset_rx_fifo <= 1'b1;
                    // Set Wishbone master signals to inactive state
                    m_wb_cyc_o <= 1'b0;
                    m_wb_stb_o <= 1'b0;
                    // Initialize address offset to 0
                    offset <= 0;
                    if (en) begin
                        // Start FIFO read operation
                        rd <= 1'b1;
                        state <= READ;
                    end
                end
                READ: begin
                    // Stop any previous read operation
                    rd <= 1'b0;
                    // Release FIFO from reset state
                    reset_rx_fifo <= 1'b0;
                    if (!empty && wb_free) begin
                        // Start reading from FIFO and initiate Wishbone write transaction
                        rd <= 1'b1;
                        m_wb_dat_o <= q;
                        m_wb_cyc_o <= 1'b1;
                        m_wb_stb_o <= 1'b1;
                        wb_free <= 1'b0;
                        state <= WRITE;
                    end
                end
                WRITE: begin
                    // Reset Wishbone control signals
                    m_wb_cyc_o <= 1'b0;
                    m_wb_stb_o <= 1'b0;
                    // Update address offset
                    offset <= offset + MEM_OFFSET;
                    // Mark Wishbone bus as free
                    wb_free <= 1'b1;
                    // Check for acknowledgment
                    if (m_wb_ack_i) begin
                        state <= READ;
                    end
                end
            endcase
        end
    end

endmodule

module sd_fifo_rx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    output reg [31:0] m_wb_dat_o,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [SD_BUS_W-1:0] dat_i,
    input wr,
    output full,
    output empty
);

    // Constants
    localparam MEM_OFFSET = 4; // Memory address offset between 2 words
    localparam FIFO_RX_MEM_DEPTH = 1024; // Width of RX Fifo
    localparam FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1; // $\log_2{FIFO\_RX\_MEM\_DEPTH}$ +1

    // Registers
    reg [8:0] offset; // Address offset for Wishbone transactions
    reg wb_free; // Wishbone bus availability flag
    reg reset_rx_fifo; // RX FIFO reset control
    reg rd; // Read enable for RX FIFO

    // Sub-modules
    sd_rx_fifo #(
        .FIFO_RX_MEM_DEPTH(FIFO_RX_MEM_DEPTH),
        .FIFO_RX_MEM_ADR_SIZE(FIFO_RX_MEM_ADR_SIZE)
    ) rx_fifo (
        .d(dat_i),
        .wr(wr),
        .wclk(sd_clk),
        .q(m_wb_dat_o),
        .rd(rd),
        .full(full),
        .empty(empty),
        .mem_empt(),
        .rclk(clk),
        .rst(reset_rx_fifo)
    );

    // State machine
    always @(posedge clk) begin
        if (rst) begin
            // Reset all control signals and counters
            offset <= 0;
            wb_free <= 1;
            reset_rx_fifo <= 1;
            rd <= 0;

            // Reset the RX FIFO buffer
            rx_fifo.reset();

            // Set Wishbone master signals to inactive state
            m_wb_adr_o <= 0;
            m_wb_we_o <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;
        end else begin
            if (en) begin
                // FIFO Read Preparation
                if (!rd && !empty) begin
                    rd <= 1;
                end

                // Read from FIFO and initiate Wishbone write transaction
                if (rd && !empty && wb_free) begin
                    // Prepare Wishbone data output
                    m_wb_dat_o <= rx_fifo.q;

                    // Set Wishbone write enable and control signals
                    m_wb_we_o <= 1;
                    m_wb_cyc_o <= 1;
                    m_wb_stb_o <= 1;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;

                    // Mark Wishbone bus as busy
                    wb_free <= 0;
                end

                // Wishbone Write Transaction Completion
                if (wb_free && m_wb_ack_i) begin
                    // Reset Wishbone control signals
                    m_wb_we_o <= 0;
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;

                    // Update address offset
                    offset <= offset + MEM_OFFSET;

                    // Mark Wishbone bus as free
                    wb_free <= 1;
                end
            } else begin
                // Reset RX FIFO
                rx_fifo.reset();

                // Stop FIFO read operation
                rd <= 0;

                // Reset address offset
                offset <= 0;

                // Reset all Wishbone control signals
                m_wb_adr_o <= 0;
                m_wb_we_o <= 0;
                m_wb_cyc_o <= 0;
                m_wb_stb_o <= 0;
                m_wb_cti_o <= 0;
                m_wb_bte_o <= 0;

                // Mark Wishbone bus as free
                wb_free <= 1;
            end
        end
    end

    // Wishbone address output
    always @(posedge clk) begin
        if (rst) begin
            m_wb_adr_o <= 0;
        end else begin
            if (en) begin
                m_wb_adr_o <= adr + offset;
            end
        end
    end

    // Wishbone write enable output
    always @(posedge clk) begin
        if (rst) begin
            m_wb_we_o <= 0;
        end else begin
            if (en) begin
                m_wb_we_o <= 1;
            end
        end
    end

    // Wishbone cycle output
    always @(posedge clk) begin
        if (rst) begin
            m_wb_cyc_o <= 0;
        end else begin
            if (en) begin
                m_wb_cyc_o <= 1;
            end
        end
    end

    // Wishbone strobe output
    always @(posedge clk) begin
        if (rst) begin
            m_wb_stb_o <= 0;
        end else begin
            if (en) begin
                m_wb_stb_o <= 1;
            end
        end
    end

    // Wishbone cycle type identifier output
    always @(posedge clk) begin
        if (rst) begin
            m_wb_cti_o <= 0;
        end else begin
            if (en) begin
                m_wb_cti_o <= 0;
            end
        end
    end

    // Wishbone burst type extension output
    always @(posedge clk) begin
        if (rst) begin
            m_wb_bte_o <= 0;
        end else begin
            if (en) begin
                m_wb_bte_o <= 0;
            end
        end
    end

    // RX FIFO reset control
    always @(posedge clk) begin
        if (rst) begin
            reset_rx_fifo <= 1;
        end else begin
            if (en) begin
                reset_rx_fifo <= 0;
            end
        end
    end

endmodule

module sd_fifo_rx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    output reg [31:0] m_wb_dat_o,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [SD_BUS_W-1:0] dat_i,
    input wr,
    output full,
    output empty
);

    parameter MEM_OFFSET = 4;
    parameter FIFO_RX_MEM_DEPTH = 1024;
    parameter FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

    reg [FIFO_RX_MEM_ADR_SIZE-1:0] rd_ptr;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] wr_ptr;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] mem_empt;
    reg [31:0] rx_fifo [FIFO_RX_MEM_DEPTH-1:0];
    reg [31:0] dat_o;
    reg [31:0] offset;
    reg [31:0] wb_free;
    reg [31:0] reset_rx_fifo;
    reg [31:0] rd;
    reg [31:0] full_reg;
    reg [31:0] empty_reg;

    assign full = full_reg;
    assign empty = empty_reg;

    always @(posedge clk) begin
        if (rst) begin
            offset <= 0;
            wb_free <= 1;
            reset_rx_fifo <= 1;
            rd <= 0;
            full_reg <= 0;
            empty_reg <= 1;
            mem_empt <= 0;
            rd_ptr <= 0;
            wr_ptr <= 0;
        end else begin
            if (en) begin
                if (wr) begin
                    rx_fifo[wr_ptr] <= dat_i;
                    wr_ptr <= wr_ptr + 1;
                    if (wr_ptr == FIFO_RX_MEM_DEPTH) begin
                        wr_ptr <= 0;
                    end
                    if (wr_ptr == rd_ptr) begin
                        full_reg <= 1;
                    end
                    empty_reg <= 0;
                    mem_empt <= FIFO_RX_MEM_DEPTH - (wr_ptr - rd_ptr);
                end
                if (rd && !wb_free && !empty_reg) begin
                    dat_o <= rx_fifo[rd_ptr];
                    rd_ptr <= rd_ptr + 1;
                    if (rd_ptr == FIFO_RX_MEM_DEPTH) begin
                        rd_ptr <= 0;
                    end
                    if (rd_ptr == wr_ptr) begin
                        empty_reg <= 1;
                    end
                    full_reg <= 0;
                    mem_empt <= FIFO_RX_MEM_DEPTH - (wr_ptr - rd_ptr);
                end
                if (wb_free && !empty_reg) begin
                    m_wb_adr_o <= adr + offset;
                    m_wb_we_o <= 1;
                    m_wb_dat_o <= dat_o;
                    m_wb_cyc_o <= 1;
                    m_wb_stb_o <= 1;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;
                    wb_free <= 0;
                    rd <= 1;
                    offset <= offset + MEM_OFFSET;
                end else begin
                    m_wb_adr_o <= 0;
                    m_wb_we_o <= 0;
                    m_wb_dat_o <= 0;
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;
                    wb_free <= 1;
                    rd <= 0;
                end
            end else begin
                reset_rx_fifo <= 1;
                rd <= 0;
                offset <= 0;
                m_wb_adr_o <= 0;
                m_wb_we_o <= 0;
                m_wb_dat_o <= 0;
                m_wb_cyc_o <= 0;
                m_wb_stb_o <= 0;
                m_wb_cti_o <= 0;
                m_wb_bte_o <= 0;
                wb_free <= 1;
            end
        end
    end

    always @(posedge sd_clk) begin
        if (reset_rx_fifo) begin
            rd_ptr <= 0;
            wr_ptr <= 0;
            mem_empt <= 0;
            reset_rx_fifo <= 0;
        end
    end

endmodule

module sd_fifo_rx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    output reg [31:0] m_wb_dat_o,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [3:0] dat_i,
    input wr,
    output full,
    output empty
);

    // Define constants
    localparam MEM_OFFSET = 4;
    localparam FIFO_RX_MEM_DEPTH = 1024;
    localparam FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

    // Define registers
    reg [8:0] offset;
    reg wb_free;
    reg reset_rx_fifo;
    reg rd;

    // Define wires
    wire [31:0] dat_o;
    wire [FIFO_RX_MEM_ADR_SIZE-1:0] rx_fifo_adr;
    wire [31:0] rx_fifo_dat;
    wire rx_fifo_wr;
    wire rx_fifo_rd;
    wire rx_fifo_full;
    wire rx_fifo_empty;

    // Instantiate sd_rx_fifo module
    sd_rx_fifo #(
        .FIFO_RX_MEM_DEPTH(FIFO_RX_MEM_DEPTH),
        .FIFO_RX_MEM_ADR_SIZE(FIFO_RX_MEM_ADR_SIZE)
    ) rx_fifo (
        .d(dat_i),
        .wr(wr),
        .wclk(sd_clk),
        .q(dat_o),
        .rd(rd),
        .full(rx_fifo_full),
        .empty(rx_fifo_empty),
        .mem_empt(),
        .rclk(clk),
        .rst(reset_rx_fifo)
    );

    // State machine
    always @(posedge clk or posedge rst) begin
        if (rst) begin
            // Reset all control signals and counters
            offset <= 0;
            wb_free <= 1;
            reset_rx_fifo <= 1;
            rd <= 0;
            m_wb_adr_o <= 0;
            m_wb_we_o <= 0;
            m_wb_dat_o <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;
        end else begin
            if (en) begin
                // FIFO Read Preparation
                if (!rx_fifo_empty && wb_free) begin
                    rd <= 1;
                end else begin
                    rd <= 0;
                end

                // Wishbone Write Transaction Completion
                if (wb_free && m_wb_ack_i) begin
                    // Reset Wishbone control signals
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;

                    // Update address offset
                    offset <= offset + MEM_OFFSET;

                    // Mark Wishbone bus as free
                    wb_free <= 1;
                end

                // Read from FIFO and initiate Wishbone write transaction
                if (!rx_fifo_empty && wb_free) begin
                    // Prepare Wishbone data output
                    m_wb_dat_o <= dat_o;

                    // Set Wishbone write enable and control signals
                    m_wb_we_o <= 1;
                    m_wb_cyc_o <= 1;
                    m_wb_stb_o <= 1;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;

                    // Mark Wishbone bus as busy
                    wb_free <= 0;
                end
            end else begin
                // Reset RX FIFO
                reset_rx_fifo <= 1;

                // Stop FIFO read operation
                rd <= 0;

                // Reset address offset
                offset <= 0;

                // Reset all Wishbone control signals
                m_wb_adr_o <= 0;
                m_wb_we_o <= 0;
                m_wb_dat_o <= 0;
                m_wb_cyc_o <= 0;
                m_wb_stb_o <= 0;
                m_wb_cti_o <= 0;
                m_wb_bte_o <= 0;

                // Mark Wishbone bus as free
                wb_free <= 1;
            end
        end
    end

    // Output assignments
    assign rx_fifo_adr = offset[FIFO_RX_MEM_ADR_SIZE-1:0];
    assign rx_fifo_dat = m_wb_dat_o;
    assign rx_fifo_wr = m_wb_we_o;
    assign rx_fifo_rd = rd;
    assign full = rx_fifo_full;
    assign empty = rx_fifo_empty;

endmodule

module sd_fifo_rx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    output reg [31:0] m_wb_dat_o,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    input [SD_BUS_W-1:0] dat_i,
    input wr,
    output full,
    output empty
);

    parameter MEM_OFFSET = 4;
    parameter FIFO_RX_MEM_DEPTH = 1024;
    parameter FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

    reg [FIFO_RX_MEM_ADR_SIZE-1:0] rx_fifo_wr_ptr;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] rx_fifo_rd_ptr;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] rx_fifo_mem_adr;
    reg [SD_BUS_W-1:0] rx_fifo_mem [0:FIFO_RX_MEM_DEPTH-1];
    reg rx_fifo_wr_en;
    reg rx_fifo_rd_en;
    wire rx_fifo_full;
    wire rx_fifo_empty;

    assign full = rx_fifo_full;
    assign empty = rx_fifo_empty;

    always @(posedge clk) begin
        if (rst) begin
            rx_fifo_wr_ptr <= 0;
            rx_fifo_rd_ptr <= 0;
            rx_fifo_mem_adr <= 0;
            rx_fifo_wr_en <= 0;
            rx_fifo_rd_en <= 0;
        end else begin
            if (wr && !rx_fifo_full) begin
                rx_fifo_mem[rx_fifo_wr_ptr] <= dat_i;
                rx_fifo_wr_ptr <= rx_fifo_wr_ptr + 1;
                rx_fifo_wr_en <= 1;
            end else begin
                rx_fifo_wr_en <= 0;
            end

            if (m_wb_ack_i && !rx_fifo_empty) begin
                m_wb_dat_o <= rx_fifo_mem[rx_fifo_rd_ptr];
                rx_fifo_rd_ptr <= rx_fifo_rd_ptr + 1;
                rx_fifo_rd_en <= 1;
            end else begin
                rx_fifo_rd_en <= 0;
            end
        end
    end

    assign rx_fifo_full = (rx_fifo_wr_ptr == rx_fifo_rd_ptr) && rx_fifo_wr_en && !rx_fifo_rd_en;
    assign rx_fifo_empty = (rx_fifo_wr_ptr == rx_fifo_rd_ptr) && !rx_fifo_wr_en && rx_fifo_rd_en;

    always @(posedge clk) begin
        if (rst) begin
            m_wb_adr_o <= 0;
            m_wb_we_o <= 0;
            m_wb_dat_o <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;
        end else begin
            if (en) begin
                if (!m_wb_ack_i && !rx_fifo_empty) begin
                    m_wb_adr_o <= adr + (rx_fifo_mem_adr * MEM_OFFSET);
                    m_wb_we_o <= 1;
                    m_wb_dat_o <= rx_fifo_mem[rx_fifo_rd_ptr];
                    m_wb_cyc_o <= 1;
                    m_wb_stb_o <= 1;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;
                end else begin
                    m_wb_adr_o <= 0;
                    m_wb_we_o <= 0;
                    m_wb_dat_o <= 0;
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;
                end
            end else begin
                m_wb_adr_o <= 0;
                m_wb_we_o <= 0;
                m_wb_dat_o <= 0;
                m_wb_cyc_o <= 0;
                m_wb_stb_o <= 0;
                m_wb_cti_o <= 0;
                m_wb_bte_o <= 0;
            end
        end
    end

endmodule

module sd_fifo_tx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    input [31:0] m_wb_dat_i,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    output reg [31:0] dat_o,
    input rd,
    output reg empty,
    output reg fe
);

    // Constants
    localparam BD_SIZE = 256;
    localparam BD_WIDTH = $clog2(BD_SIZE);
    localparam FIFO_TX_MEM_DEPTH = 256;
    localparam FIFO_TX_MEM_ADR_SIZE = $clog2(FIFO_TX_MEM_DEPTH) + 1;

    // Registers
    reg [BD_WIDTH-1:0] offset;
    reg [BD_WIDTH-1:0] we;
    reg [31:0] din;
    reg wr_tx;
    reg reset_tx_fifo;
    reg first;
    reg ackd;
    reg delay;

    // Wishbone master signals
    assign m_wb_adr_o = {adr[31:2], offset[BD_WIDTH-1:2], 2'b00};
    assign m_wb_we_o = 0;
    assign m_wb_cti_o = 0;
    assign m_wb_bte_o = 0;

    // TX FIFO signals
    wire [31:0] q;
    wire full;
    wire empty_fifo;
    wire [5:0] mem_empt;

    sd_tx_fifo #(
        .FIFO_DEPTH(FIFO_TX_MEM_DEPTH),
        .FIFO_ADR_SIZE(FIFO_TX_MEM_ADR_SIZE)
    ) tx_fifo (
        .d(din),
        .wr(wr_tx),
        .wclk(clk),
        .q(q),
        .rd(rd),
        .full(full),
        .empty(empty_fifo),
        .mem_empt(mem_empt),
        .rclk(sd_clk),
        .rst(reset_tx_fifo)
    );

    // State machine
    reg [1:0] state;
    localparam IDLE = 2'b00;
    localparam READ = 2'b01;
    localparam WRITE = 2'b10;

    always @(posedge clk) begin
        if (rst) begin
            // Reset all registers and signals
            offset <= 0;
            we <= 0;
            din <= 0;
            wr_tx <= 0;
            reset_tx_fifo <= 1;
            first <= 1;
            ackd <= 0;
            delay <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_we_o <= 0;
            state <= IDLE;
            empty <= 1;
            fe <= 0;
        end else begin
            // Default values
            wr_tx <= 0;
            reset_tx_fifo <= 0;
            delay <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_we_o <= 0;
            empty <= 0;
            fe <= 0;

            // State machine
            case (state)
                IDLE: begin
                    if (en && !full) begin
                        // Start a Wishbone read transaction
                        m_wb_cyc_o <= 1;
                        m_wb_stb_o <= 1;
                        m_wb_we_o <= 0;
                        state <= READ;
                    end
                end
                READ: begin
                    if (m_wb_ack_i) begin
                        // Wishbone transaction acknowledged
                        din <= m_wb_dat_i;
                        wr_tx <= 1;
                        m_wb_cyc_o <= 0;
                        m_wb_stb_o <= 0;
                        m_wb_we_o <= 0;
                        state <= WRITE;
                        delay <= 1;
                    end
                end
                WRITE: begin
                    if (delay) begin
                        // Increment memory address offset
                        offset <= offset + 1;
                        ackd <= ~ackd;
                        wr_tx <= 0;
                        delay <= 0;
                        state <= IDLE;
                    end
                end
            endcase

            // Update FIFO empty flag
            empty <= empty_fifo;

            // Update FIFO full flag
            fe <= full;
        end
    end

endmodule

module sd_fifo_tx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    input [31:0] m_wb_dat_i,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    output reg [31:0] dat_o,
    input rd,
    output reg empty,
    output reg fe
);

    // Define constants
    localparam BD_SIZE = 256;
    localparam BD_WIDTH = $clog2(BD_SIZE);
    localparam FIFO_RX_MEM_DEPTH = 256;
    localparam FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;
    localparam FIFO_TX_MEM_DEPTH = 256;
    localparam FIFO_TX_MEM_ADR_SIZE = $clog2(FIFO_TX_MEM_DEPTH) + 1;

    // Define registers
    reg [BD_WIDTH-1:0] offset;
    reg [BD_WIDTH-1:0] we;
    reg [31:0] din;
    reg wr_tx;
    reg reset_tx_fifo;
    reg first;
    reg ackd;
    reg delay;

    // Define wires
    wire [31:0] q;
    wire full;
    wire [FIFO_RX_MEM_ADR_SIZE-1:0] mem_empt;

    // Instantiate sd_tx_fifo module
    sd_tx_fifo #(
        .FIFO_DEPTH(FIFO_TX_MEM_DEPTH)
    ) tx_fifo (
        .d(din),
        .wr(wr_tx),
        .wclk(sd_clk),
        .q(q),
        .rd(rd),
        .full(full),
        .empty(empty),
        .mem_empt(mem_empt),
        .rclk(clk),
        .rst(reset_tx_fifo)
    );

    // State machine
    always @(posedge clk) begin
        if (rst) begin
            // Reset all registers and counters
            offset <= 0;
            we <= 0;
            din <= 0;
            wr_tx <= 0;
            reset_tx_fifo <= 1;
            first <= 1;
            ackd <= 0;
            delay <= 0;

            // Reset Wishbone signals
            m_wb_adr_o <= 0;
            m_wb_we_o <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_cti_o <= 0;
            m_wb_bte_o <= 0;

            // Set TX FIFO reset control
            reset_tx_fifo <= 1;
        end else begin
            if (en) begin
                // Start a Wishbone read transaction
                if (!full && !m_wb_ack_i && ackd) begin
                    m_wb_adr_o <= adr + offset;
                    m_wb_we_o <= 0;
                    m_wb_cyc_o <= 1;
                    m_wb_stb_o <= 1;
                    m_wb_cti_o <= 0;
                    m_wb_bte_o <= 0;
                    we <= we + 1;
                    first <= 0;
                end

                // Write data to FIFO
                if (m_wb_ack_i && !first) begin
                    din <= m_wb_dat_i;
                    wr_tx <= 1;
                    delay <= 1;
                end

                // Stop writing to FIFO
                if (delay) begin
                    wr_tx <= 0;
                    delay <= 0;
                end

                // Increment memory address offset
                if (delay) begin
                    offset <= offset + 1;
                    ackd <= ~ackd;
                }
            } else begin
                // Reset TX FIFO
                reset_tx_fifo <= 1;

                // Reset address offset
                offset <= 0;

                // Reset Wishbone signals
                m_wb_adr_o <= 0;
                m_wb_we_o <= 0;
                m_wb_cyc_o <= 0;
                m_wb_stb_o <= 0;
                m_wb_cti_o <= 0;
                m_wb_bte_o <= 0;
            end
        end
    end

    // Set TX FIFO reset control
    always @(posedge clk) begin
        if (rst) begin
            reset_tx_fifo <= 1;
        end else begin
            if (en) begin
                reset_tx_fifo <= 0;
            end else begin
                reset_tx_fifo <= 1;
            end
        end
    end

    // Set TX FIFO full flag
    always @(posedge clk) begin
        if (rst) begin
            fe <= 0;
        end else begin
            if (en) begin
                fe <= full;
            end else begin
                fe <= 0;
            end
        end
    end

    // Set TX FIFO data output
    always @(posedge sd_clk) begin
        if (rd) begin
            dat_o <= q;
        end
    end

endmodule

module sd_fifo_tx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    input [31:0] m_wb_dat_i,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    output reg [31:0] dat_o,
    input rd,
    output reg empty,
    output reg fe
);

    // Registers
    reg [8:0] offset;
    reg [8:0] we;
    reg [31:0] din;
    reg wr_tx;
    reg reset_tx_fifo;
    reg first;
    reg ackd;
    reg delay;

    // Wishbone signals
    assign m_wb_adr_o = {adr[31:2], offset[8:2]};
    assign m_wb_we_o = 0;
    assign m_wb_cyc_o = m_wb_stb_o;
    assign m_wb_cti_o = 0;
    assign m_wb_bte_o = 0;

    // TX FIFO signals
    wire [31:0] q;
    wire full;
    wire empty;
    wire [5:0] mem_empt;

    sd_tx_fifo fifo (
        .d(din),
        .wr(wr_tx),
        .wclk(clk),
        .q(q),
        .rd(rd),
        .full(full),
        .empty(empty),
        .mem_empt(mem_empt),
        .rclk(sd_clk),
        .rst(reset_tx_fifo)
    );

    // State machine
    parameter IDLE = 2'b00;
    parameter READ = 2'b01;
    parameter WRITE = 2'b10;
    reg [1:0] state;

    always @(posedge clk) begin
        if (rst) begin
            // Reset all control signals and counters
            offset <= 0;
            we <= 0;
            din <= 0;
            wr_tx <= 0;
            reset_tx_fifo <= 1;
            first <= 1;
            ackd <= 0;
            delay <= 0;
            state <= IDLE;
        } else begin
            case (state)
                IDLE: begin
                    // Reset TX FIFO
                    reset_tx_fifo <= 1;

                    // Reset address offset to 0
                    offset <= 0;

                    // Set Wishbone control signals to inactive state
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_we_o <= 0;

                    // Set state to READ
                    state <= READ;
                end
                READ: begin
                    // Start a Wishbone read transaction if:
                    // - FIFO is not full
                    // - No ongoing Wishbone transaction
                    // - Previous transaction is acknowledged
                    if (!full && !m_wb_ack_i && ackd) begin
                        // Set Wishbone control signals to active state
                        m_wb_cyc_o <= 1;
                        m_wb_stb_o <= 1;
                        m_wb_we_o <= 0;

                        // Set state to WRITE
                        state <= WRITE;
                    end
                end
                WRITE: begin
                    // Write data to FIFO
                    wr_tx <= 1;
                    din <= m_wb_dat_i;

                    // Deassert Wishbone signals
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_we_o <= 0;

                    // Set delay flag for timing purposes
                    delay <= 1;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Reset address offset to 0
            offset <= 0;

            // Reset write enable counter
            we <= 0;

            // Reset first operation flag
            first <= 1;

            // Reset acknowledge delay flag
            ackd <= 0;

            // Reset delay flag
            delay <= 0;
        } else begin
            case (state)
                IDLE: begin
                    // Reset address offset to 0
                    offset <= 0;

                    // Reset write enable counter
                    we <= 0;

                    // Reset first operation flag
                    first <= 1;

                    // Reset acknowledge delay flag
                    ackd <= 0;

                    // Reset delay flag
                    delay <= 0;
                end
                READ: begin
                    // Increment memory address offset
                    offset <= offset + 1;

                    // Toggle acknowledge flag
                    ackd <= ~ackd;

                    // Stop writing to FIFO
                    wr_tx <= 0;
                end
                WRITE: begin
                    // Increment write enable counter
                    we <= we + 1;

                    // Set first operation flag
                    first <= 0;

                    // Set delay flag for timing purposes
                    delay <= 1;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Reset TX FIFO
            reset_tx_fifo <= 1;
        } else begin
            case (state)
                IDLE: begin
                    // Reset TX FIFO
                    reset_tx_fifo <= 1;
                end
                READ: begin
                    // Reset TX FIFO
                    reset_tx_fifo <= 1;
                end
                WRITE: begin
                    // Reset TX FIFO
                    reset_tx_fifo <= 1;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Set Wishbone control signals to inactive state
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_we_o <= 0;
        } else begin
            case (state)
                IDLE: begin
                    // Set Wishbone control signals to inactive state
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_we_o <= 0;
                end
                READ: begin
                    // Set Wishbone control signals to active state
                    m_wb_cyc_o <= 1;
                    m_wb_stb_o <= 1;
                    m_wb_we_o <= 0;
                end
                WRITE: begin
                    // Set Wishbone control signals to inactive state
                    m_wb_cyc_o <= 0;
                    m_wb_stb_o <= 0;
                    m_wb_we_o <= 0;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Set state to IDLE
            state <= IDLE;
        end else begin
            case (state)
                IDLE: begin
                    // Set state to READ
                    state <= READ;
                end
                READ: begin
                    // Set state to WRITE
                    state <= WRITE;
                end
                WRITE: begin
                    // Set state to READ
                    state <= READ;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Set TX FIFO full flag to 0
            fe <= 0;
        } else begin
            case (state)
                IDLE: begin
                    // Set TX FIFO full flag to 0
                    fe <= 0;
                end
                READ: begin
                    // Set TX FIFO full flag to 0
                    fe <= 0;
                end
                WRITE: begin
                    // Set TX FIFO full flag to 0
                    fe <= 0;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Set TX FIFO empty flag to 1
            empty <= 1;
        } else begin
            case (state)
                IDLE: begin
                    // Set TX FIFO empty flag to 1
                    empty <= 1;
                end
                READ: begin
                    // Set TX FIFO empty flag to 1
                    empty <= 1;
                end
                WRITE: begin
                    // Set TX FIFO empty flag to 1
                    empty <= 1;
                end
            endcase
        end
    end

    always @(posedge clk) begin
        if (rst) begin
            // Set TX FIFO data output to 0
            dat_o <= 0;
        } else begin
            case (state)
                IDLE: begin
                    // Set TX FIFO data output to 0
                    dat_o <= 0;
                end
                READ: begin
                    // Set TX FIFO data output to 0
                    dat_o <= 0;
                end
                WRITE: begin
                    // Set TX FIFO data output to 0
                    dat_o <= 0;
                end
            endcase
        end
    end

endmodule

module sd_fifo_tx_filler (
    input clk,
    input rst,
    output reg [31:0] m_wb_adr_o,
    output reg m_wb_we_o,
    input [31:0] m_wb_dat_i,
    output reg m_wb_cyc_o,
    output reg m_wb_stb_o,
    input m_wb_ack_i,
    output reg [2:0] m_wb_cti_o,
    output reg [1:0] m_wb_bte_o,
    input en,
    input [31:0] adr,
    input sd_clk,
    output reg [31:0] dat_o,
    input rd,
    output reg empty,
    output reg fe
);

    // Registers
    reg [8:0] offset;
    reg [8:0] we;
    reg [31:0] din;
    reg wr_tx;
    reg reset_tx_fifo;
    reg first;
    reg ackd;
    reg delay;

    // Wishbone signals
    assign m_wb_adr_o = {adr[31:2], offset};
    assign m_wb_we_o = 0;
    assign m_wb_cti_o = 0;
    assign m_wb_bte_o = 0;

    // FIFO signals
    wire [31:0] q;
    wire full;
    wire empty_fifo;
    wire [5:0] mem_empt;

    // Instantiate FIFO
    sd_tx_fifo fifo (
        .d(din),
        .wr(wr_tx),
        .wclk(clk),
        .q(q),
        .rd(rd),
        .full(full),
        .empty(empty_fifo),
        .mem_empt(mem_empt),
        .rclk(sd_clk),
        .rst(reset_tx_fifo)
    );

    // State machine
    parameter IDLE = 2'b00;
    parameter READ = 2'b01;
    parameter WRITE = 2'b10;
    reg [1:0] state;

    always @(posedge clk) begin
        if (rst) begin
            // Reset all registers and signals
            offset <= 0;
            we <= 0;
            din <= 0;
            wr_tx <= 0;
            reset_tx_fifo <= 1;
            first <= 1;
            ackd <= 0;
            delay <= 0;
            m_wb_cyc_o <= 0;
            m_wb_stb_o <= 0;
            m_wb_we_o <= 0;
            state <= IDLE;
            empty <= 1;
            fe <= 0;
        end else begin
            case (state)
                IDLE: begin
                    if (en) begin
                        // Start a Wishbone read transaction
                        m_wb_cyc_o <= 1;
                        m_wb_stb_o <= 1;
                        m_wb_we_o <= 0;
                        state <= READ;
                    end
                end
                READ: begin
                    if (m_wb_ack_i) begin
                        // Write data to FIFO
                        wr_tx <= 1;
                        din <= m_wb_dat_i;
                        // Deassert Wishbone signals
                        m_wb_cyc_o <= 0;
                        m_wb_stb_o <= 0;
                        m_wb_we_o <= 0;
                        // Set delay flag for timing purposes
                        delay <= 1;
                        state <= WRITE;
                    end
                end
                WRITE: begin
                    if (delay) begin
                        // Increment memory address offset
                        offset <= offset + 1;
                        // Toggle acknowledge flag
                        ackd <= ~ackd;
                        // Stop writing to FIFO
                        wr_tx <= 0;
                        delay <= 0;
                        state <= IDLE;
                    end
                end
            endcase
            // Update FIFO empty flag
            empty <= empty_fifo;
            // Update FIFO full flag
            fe <= full;
        end
    end

endmodule

module sd_rx_fifo (
    input [3:0] d,
    input wr,
    input wclk,
    output [31:0] q,
    input rd,
    output full,
    output empty,
    output [1:0] mem_empt,
    input rclk,
    input rst
);

    parameter FIFO_RX_MEM_DEPTH = 32;
    parameter FIFO_RX_MEM_ADR_SIZE = $clog2(FIFO_RX_MEM_DEPTH) + 1;

    reg [31:0] ram [0:FIFO_RX_MEM_DEPTH-1];
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_i;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_o;
    reg [7:0] we;
    reg [31:0] tmp;
    reg ft;

    assign full = (adr_i == adr_o) && ft;
    assign empty = (adr_i == adr_o) && !ft;
    assign mem_empt = FIFO_RX_MEM_DEPTH - adr_i + adr_o;

    always @(posedge wclk) begin
        if (wr && !full) begin
            tmp <= tmp | {d, tmp[31:4]};
            if (tmp[3:0] == 4'hF) begin
                ram[adr_i] <= tmp;
                adr_i <= adr_i + 1;
                we <= 8'h1;
            end else begin
                we <= we << 4;
            end
        end
    end

    always @(posedge rclk) begin
        if (rd && !empty) begin
            q <= ram[adr_o];
            adr_o <= adr_o + 1;
        end
    end

    always @(posedge wclk) begin
        if (wr) begin
            ft <= 1;
        end else if (rd && !empty) begin
            ft <= 0;
        end
    end

    always @(posedge rst) begin
        if (rst) begin
            adr_i <= 0;
            adr_o <= 0;
            we <= 8'h1;
            tmp <= 0;
            ft <= 0;
        end
    end

endmodule

