module sd_tx_fifo(
    input [31:0] d,
    input wr,
    input wclk,
    output [31:0] q,
    input rd,
    output full,
    output empty,
    output [5:0] mem_empt,
    input rclk,
    input rst
);

    reg [31:0] ram [0:FIFO_TX_MEM_DEPTH-1];
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_i;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_o;
    wire [FIFO_RX_MEM_ADR_SIZE-1:0] adr_i_next;
    wire [FIFO_RX_MEM_ADR_SIZE-1:0] adr_o_next;
    wire [5:0] mem_empt_next;
    wire [31:0] q_next;
    wire full_next;
    wire empty_next;

    assign adr_i_next = (wr && !full) ? adr_i + 1'b1 : adr_i;
    assign adr_o_next = (rd && !empty) ? adr_o + 1'b1 : adr_o;
    assign mem_empt_next = (adr_i_next == adr_o_next) ? 6'd0 : (adr_i_next > adr_o_next) ? (adr_i_next - adr_o_next) : (FIFO_TX_MEM_DEPTH - adr_o_next + adr_i_next);
    assign q_next = ram[adr_o];
    assign full_next = (adr_i_next == adr_o_next) ? 1'b1 : 1'b0;
    assign empty_next = (adr_i_next == adr_o_next) ? 1'b1 : 1'b0;

    always @(posedge wclk) begin
        if (rst) begin
            adr_i <= 0;
            adr_o <= 0;
        end else begin
            adr_i <= adr_i_next;
            adr_o <= adr_o_next;
            if (wr && !full) begin
                ram[adr_i] <= d;
            end
        end
    end

    always @(posedge rclk) begin
        if (rst) begin
            q <= 0;
            full <= 0;
            empty <= 1;
            mem_empt <= 0;
        end else begin
            q <= q_next;
            full <= full_next;
            empty <= empty_next;
            mem_empt <= mem_empt_next;
        end
    end

endmodule

module sd_tx_fifo (
    input [31:0] d,
    input wr,
    input wclk,
    output [31:0] q,
    input rd,
    output full,
    output empty,
    output [5:0] mem_empt,
    input rclk,
    input rst
);

    reg [31:0] ram [0:FIFO_TX_MEM_DEPTH-1];
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_i;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_o;

    always @(posedge wclk) begin
        if (wr && !full) begin
            ram[adr_i] <= d;
            adr_i <= adr_i + 1;
        end
    end

    always @(posedge rclk) begin
        if (rd && !empty) begin
            q <= ram[adr_o];
            adr_o <= adr_o + 1;
        end
    end

    assign full = (adr_i == adr_o) && (adr_i != 0);
    assign empty = (adr_i == adr_o) && (adr_i == 0);
    assign mem_empt = adr_i - adr_o;

    always @(posedge rst) begin
        if (rst) begin
            adr_i <= 0;
            adr_o <= 0;
        end
    end

endmodule

module sd_tx_fifo (
    input [31:0] d,
    input wr,
    input wclk,
    output [31:0] q,
    input rd,
    output full,
    output empty,
    output [5:0] mem_empt,
    input rclk,
    input rst
);

    reg [31:0] ram [0:FIFO_TX_MEM_DEPTH-1];
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_i;
    reg [FIFO_RX_MEM_ADR_SIZE-1:0] adr_o;
    wire [FIFO_RX_MEM_ADR_SIZE-1:0] adr_i_next;
    wire [FIFO_RX_MEM_ADR_SIZE-1:0] adr_o_next;
    wire [5:0] mem_empt_next;
    wire full_next;
    wire empty_next;

    assign adr_i_next = (wr && !full) ? adr_i + 1 : adr_i;
    assign adr_o_next = (rd && !empty) ? adr_o + 1 : adr_o;
    assign mem_empt_next = adr_i_next - adr_o_next;
    assign full_next = (mem_empt_next == FIFO_TX_MEM_DEPTH);
    assign empty_next = (adr_i_next == adr_o_next);

    always @(posedge wclk) begin
        if (rst) begin
            adr_i <= 0;
            adr_o <= 0;
        end else begin
            adr_i <= adr_i_next;
            adr_o <= adr_o_next;
            if (wr && !full) begin
                ram[adr_i] <= d;
            end
        end
    end

    always @(posedge rclk) begin
        if (rst) begin
            q <= 0;
            full <= 0;
            empty <= 1;
            mem_empt <= 0;
        end else begin
            if (rd && !empty) begin
                q <= ram[adr_o];
            end
            full <= full_next;
            empty <= empty_next;
            mem_empt <= mem_empt_next;
        end
    end

endmodule

